// Content Generation Keyword Analyzer
const allInput = $input.all();
const timestamp = new Date().toISOString().split('T')[0];
let allKeywords = [];
const seenKeywords = new Set();

// Extract and deduplicate keywords from all sources
allInput.forEach((item, sectionIndex) => {
  const section = item.json;

  if (!section || !section.keywords || !Array.isArray(section.keywords)) {
    return;
  }

  section.keywords.forEach(kw => {
    // Handle both string and object keyword formats
    const keywordText = typeof kw === 'string' ? kw : kw.keyword;
    const keywordLower = keywordText.toLowerCase().trim();

    // Skip duplicates
    if (seenKeywords.has(keywordLower)) {
      return;
    }
    seenKeywords.add(keywordLower);

    // For object keywords with full metrics
    if (typeof kw === 'object' && kw.keyword) {
      // Calculate opportunity score (0-100)
      const volumeScore = Math.min((kw.volume || 0) / 10000, 1) * 40;
      const difficultyScore = (100 - (kw.difficulty || 0)) / 100 * 40;
      const cpcScore = Math.min((kw.cpc || 0) / 20, 1) * 20;
      const opportunityScore = Math.round(volumeScore + difficultyScore + cpcScore);

      // Determine search intent
      let intent = 'informational';
      const kwText = keywordText.toLowerCase();

      if (kwText.match(/\\b(buy|purchase|price|cost|cheap|deal|discount|order)\\b/)) {
        intent = 'transactional';
      } else if (kwText.match(/\\b(best|top|review|vs|versus|compare|alternative)\\b/)) {
        intent = 'commercial';
      } else if (kwText.match(/\\b(how|what|why|when|where|guide|tutorial|tips|learn)\\b/)) {
        intent = 'informational';
      } else if (kwText.match(/\\b(login|sign up|download|free trial|official)\\b/)) {
        intent = 'navigational';
      }

      // Suggest content type
      let contentType = 'blog post';

      if (kwText.startsWith('how to') || kwText.includes('step by step')) {
        contentType = 'how-to guide';
      } else if (kwText.startsWith('what is') || kwText.startsWith('why is')) {
        contentType = 'explainer article';
      } else if (kwText.match(/\\?$/)) {
        contentType = 'FAQ / Q&A';
      } else if (kwText.match(/\\b(best|top [0-9]|top ten)\\b/)) {
        contentType = 'listicle';
      } else if (kwText.match(/\\b(vs|versus|compare|comparison)\\b/)) {
        contentType = 'comparison article';
      } else if (kwText.includes('guide') || kwText.includes('complete')) {
        contentType = 'comprehensive guide';
      } else if (kwText.match(/\\b(review|reviews)\\b/)) {
        contentType = 'review article';
      } else if (kwText.match(/\\b(tips|strategies|tactics|ideas)\\b/)) {
        contentType = 'tips & tactics';
      }

      // Analyze trend
      let trendStatus = 'stable';
      let trendAvg = 0;
      let trendDirection = 0;

      if (kw.history_trend && typeof kw.history_trend === 'object') {
        const trendValues = Object.values(kw.history_trend);
        if (trendValues.length >= 6) {
          const recent3 = trendValues.slice(-3).reduce((a, b) => a + b, 0) / 3;
          const older3 = trendValues.slice(0, 3).reduce((a, b) => a + b, 0) / 3;
          trendAvg = Math.round(trendValues.reduce((a, b) => a + b, 0) / trendValues.length);

          if (recent3 > older3 * 1.3) {
            trendStatus = 'trending up';
            trendDirection = Math.round(((recent3 - older3) / older3) * 100);
          } else if (recent3 < older3 * 0.7) {
            trendStatus = 'trending down';
            trendDirection = Math.round(((recent3 - older3) / older3) * 100);
          }
        }
      }

      // Calculate estimated monthly traffic (CTR assumptions)
      let estimatedTraffic = 0;
      if (kw.volume) {
        // Assume 30% CTR for position 1, 15% for 2-3, 10% for 4-5, 5% for 6-10
        // For content generation, estimate top 5 ranking potential
        estimatedTraffic = Math.round(kw.volume * 0.15);
      }

      // Priority classification
      let priority = 'LOW';
      if (opportunityScore >= 70) {
        priority = 'HIGH';
      } else if (opportunityScore >= 50) {
        priority = 'MEDIUM';
      }

      // Extract SERP features and intents if available
      const serpFeatures = kw.serp_features ? 
        (Array.isArray(kw.serp_features) ? kw.serp_features.join('; ') : kw.serp_features) : '';
      const intents = kw.intents ? 
        (Array.isArray(kw.intents) ? kw.intents.join('; ') : kw.intents) : intent;

      allKeywords.push({
        date: timestamp,
        keyword: keywordText,
        volume: kw.volume || 0,
        cpc: kw.cpc || 0,
        difficulty: kw.difficulty || 0,
        competition: kw.competition || 0,
        relevance: kw.relevance || null,
        opportunity_score: opportunityScore,
        priority: priority,
        search_intent: intents || intent,
        content_type: contentType,
        trend_status: trendStatus,
        trend_direction_pct: trendDirection,
        trend_avg_volume: trendAvg,
        estimated_monthly_traffic: estimatedTraffic,
        serp_features: serpFeatures,
        section_source: sectionIndex === 0 ? 'longtail' : 
                       sectionIndex === 1 ? 'questions' : 
                       sectionIndex === 2 ? 'similar' : 'related'
      });
    }
    // For simple string keywords
    else {
      allKeywords.push({
        date: timestamp,
        keyword: keywordText,
        volume: 0,
        cpc: 0,
        difficulty: 0,
        competition: 0,
        relevance: null,
        opportunity_score: 0,
        priority: 'UNKNOWN',
        search_intent: 'unknown',
        content_type: 'blog post',
        trend_status: 'unknown',
        trend_direction_pct: 0,
        trend_avg_volume: 0,
        estimated_monthly_traffic: 0,
        serp_features: '',
        section_source: sectionIndex === 0 ? 'longtail' : 
                       sectionIndex === 1 ? 'questions' : 
                       sectionIndex === 2 ? 'similar' : 'related'
      });
    }
  });
});

// Sort by opportunity score descending
allKeywords.sort((a, b) => b.opportunity_score - a.opportunity_score);

return allKeywords.map(kw => ({ json: kw }));
