const output = [];

function addHeader(title) {
  output.push({ json: { A: '=== ' + title + ' ===' } });
}

function addSpace() {
  for (let i = 0; i < 5; i++) {
    output.push({ json: { A: '' } });
  }
}

// 1. SUMMARY
addHeader('SUMMARY');
const summaryData = $('Get backlinks summary').first().json.summary || [];
if (summaryData.length > 0) {
  output.push({ json: { A: 'target', B: 'backlinks', C: 'refdomains', D: 'dofollow', E: 'nofollow', F: 'inlink_rank', G: 'domain_inlink_rank' } });
  for (const item of summaryData) {
    output.push({
      json: {
        A: item.target || '',
        B: item.backlinks || 0,
        C: item.refdomains || 0,
        D: item.dofollow_backlinks || 0,
        E: item.nofollow_backlinks || 0,
        F: item.inlink_rank || 0,
        G: item.domain_inlink_rank || 0
      }
    });
  }
}

addSpace();

// 2. AUTHORITY
addHeader('AUTHORITY');
const authorityData = $('Get authority metrics').first().json.pages || [];
if (authorityData.length > 0) {
  output.push({ json: { A: 'url', B: 'inlink_rank', C: 'domain_inlink_rank' } });
  for (const item of authorityData) {
    output.push({
      json: {
        A: item.url || '',
        B: item.inlink_rank || 0,
        C: item.domain_inlink_rank || 0
      }
    });
  }
}

addSpace();

// 3. NEW BACKLINKS
addHeader('NEW BACKLINKS');
const newData = $('Get new backlinks').first().json.new_lost_backlinks || [];
if (newData.length > 0) {
  output.push({ json: { A: 'date', B: 'url_from', C: 'url_to', D: 'anchor', E: 'nofollow', F: 'domain_inlink_rank' } });
  for (const item of newData) {
    output.push({
      json: {
        A: item.new_lost_date || '',
        B: item.url_from || '',
        C: item.url_to || '',
        D: item.anchor || '',
        E: item.nofollow ? 'Yes' : 'No',
        F: item.domain_inlink_rank || 0
      }
    });
  }
}

addSpace();

// 4. LOST BACKLINKS
addHeader('LOST BACKLINKS');
const lostData = $('Get lost backlinks').first().json.new_lost_backlinks || [];
if (lostData.length > 0) {
  output.push({ json: { A: 'date', B: 'url_from', C: 'url_to', D: 'anchor', E: 'reason_lost' } });
  for (const item of lostData) {
    output.push({
      json: {
        A: item.new_lost_date || '',
        B: item.url_from || '',
        C: item.url_to || '',
        D: item.anchor || '',
        E: item.reason_lost || ''
      }
    });
  }
}

addSpace();

// 5. REFERRING DOMAINS
addHeader('REFERRING DOMAINS');
const refData = $('Get referring domains').first().json.refdomains || [];
if (refData.length > 0) {
  output.push({ json: { A: 'refdomain', B: 'backlinks', C: 'dofollow', D: 'domain_inlink_rank', E: 'first_seen' } });
  for (const item of refData) {
    output.push({
      json: {
        A: item.refdomain || '',
        B: item.backlinks || 0,
        C: item.dofollow_backlinks || 0,
        D: item.domain_inlink_rank || 0,
        E: item.first_seen || ''
      }
    });
  }
}

addSpace();

// 6. ANCHOR TEXTS
addHeader('ANCHOR TEXTS');
const anchorData = $('Get anchor texts').first().json.anchors || [];
if (anchorData.length > 0) {
  output.push({ json: { A: 'anchor', B: 'backlinks', C: 'refdomains', D: 'dofollow' } });
  for (const item of anchorData) {
    output.push({
      json: {
        A: item.anchor || '[empty]',
        B: item.backlinks || 0,
        C: item.refdomains || 0,
        D: item.dofollow_backlinks || 0
      }
    });
  }
}

addSpace();

// 7. DAILY HISTORY
addHeader('DAILY HISTORY');
const dailyData = $('Get daily backlinks count').first().json.new_lost_backlinks_count || [];
if (dailyData.length > 0) {
  output.push({ json: { A: 'date', B: 'new', C: 'lost', D: 'net' } });
  for (const item of dailyData) {
    output.push({
      json: {
        A: item.date || '',
        B: item.new || 0,
        C: item.lost || 0,
        D: (item.new || 0) - (item.lost || 0)
      }
    });
  }
}

addSpace();

// 8. CUMULATIVE HISTORY
addHeader('CUMULATIVE HISTORY');
const cumulativeData = $('Get cumulative history').first().json.backlinks_count || [];
if (cumulativeData.length > 0) {
  output.push({ json: { A: 'date', B: 'backlinks' } });
  for (const item of cumulativeData) {
    output.push({
      json: {
        A: item.date || '',
        B: item.backlinks || 0
      }
    });
  }
}

return output;
