parameters.jsCode:
const LAUNCH_HINTS = [
  "launch",
  "released",
  "soft launch",
  "beta",
  "v1",
  "platform",
  "tool",
  "server",
  "api"
];

return items.filter(item => {
  const title = (item.json.title || "").toLowerCase();
  const text = (item.json.story_text || "").toLowerCase();

  return LAUNCH_HINTS.some(k => (title + " " + text).includes(k));
});

return items.map(item => {
  const title = item.json.title || "";
  const text = item.json.story_text || "";

  // Extract product name (basic heuristic)
  const productName = title
    .replace(/^Show HN:\s*/i, "")
    .split("‚Äì")[0]
    .split("-")[0]
    .trim();

  // Build HN discussion URL
  const hnUrl = item.json.objectID
    ? `https://news.ycombinator.com/item?id=${item.json.objectID}`
    : "";

  // Launch strength logic
  let launchStrength = "medium";
  if (item.json.points > 1500) launchStrength = "high";
  if (text.toLowerCase().includes("soft launch")) launchStrength = "low";

  return {
    json: {
      source: "Hacker News",
      launch_type: "Show HN",

      title: title,
      product_name: productName,

      description: text
        ? text.replace(/<[^>]+>/g, "").slice(0, 300)
        : "",

      url: item.json.url || "",
      hn_item_url: hnUrl,

      author: item.json.author,
      created_at: item.json.created_at,

      points: item.json.points,
      num_comments: item.json.num_comments,

      launch_signal_strength: launchStrength,
      detected_at: new Date().toISOString()
    }
  };
});

const raw = items[0].json.output;

// Parse the AI JSON string
const parsed = JSON.parse(raw);

return [
  {
    json: {
      slack_message: parsed.slack_message,
      email_digest: parsed.email_digest
    }
  }
];
