parameters.jsonBody:
={
  "query": "query Transcripts($fromDate: DateTime) { transcripts(fromDate: $fromDate) { id title dateString } }",
  "variables": {
    "fromDate": "{{ $today.format('YYYY-MM-DD') }}T00:00:00.000Z"
  }
}

parameters.jsCode:
// Get the response data
const response = $input.first().json;

// Access the transcripts array from the GraphQL response
// GraphQL returns data in a 'data' object
const meetings = response.data?.transcripts || [];

// Get today's date at midnight in local timezone
const today = new Date();
today.setHours(0, 0, 0, 0);
const tomorrow = new Date(today);
tomorrow.setDate(tomorrow.getDate() + 1);

// Filter items to only include today's meetings
const todaysMeetings = meetings.filter(meeting => {
  const dateString = meeting.dateString;
  if (!dateString) return false;

  const meetingDate = new Date(dateString);
  return meetingDate >= today && meetingDate < tomorrow;
});

return todaysMeetings;

parameters.jsonBody:
={
  "query": "query Transcript($id: String!) { transcript(id: $id) { id title dateString sentences { text speaker_name } summary { action_items } meeting_attendees { email name } } }",
  "variables": {
    "id": "{{ $json.id }}"
  }
}

parameters.jsCode:
// Get the input data (GraphQL response structure)
const item = $input.first();
const transcriptData = item.json.data?.transcript;

if (!transcriptData) {
  return [];
}

// Build full transcript from sentences array
let fullTranscript = '';
if (transcriptData.sentences && Array.isArray(transcriptData.sentences)) {
  fullTranscript = transcriptData.sentences
    .map(s => `${s.speaker_name}: ${s.text}`)
    .join('\n');
}

// Extract details
const title = transcriptData.title || 'Unknown Meeting';
const dateString = transcriptData.dateString || 'Unknown Date';
const attendees = transcriptData.meeting_attendees || [];
const summary = transcriptData.summary || {};

// --- CUSTOMIZATION REQUIRED ---
// Add your own name aliases here to prevent the AI from treating you as the external guest
const hostNames = ['Host', 'My Name', 'Me', 'TJ']; 

// Identify External Participant (Simple logic: first person who isn't the host)
const externalParticipant = attendees
  .map(a => a.name || a.email)
  .find(name => name && !hostNames.some(host => name.includes(host))) || 'External Participant';

// Find email (exclude internal domain)
const externalEmail = attendees
  .map(a => a.email)
  // REPLACE 'yourcompany.com' below with your actual domain
  .find(email => email && !email.includes('yourcompany.com')) || 'user@example.com';

return [{
  json: {
    title,
    dateString,
    fullTranscript,
    externalParticipant,
    externalEmail,
    actionItems: summary.action_items || '',
    originalData: transcriptData
  }
}];
