parameters.jsCode:
// Declare books
const books = [
  {
    "title": "Dune",
    "author": "Frank Herbert",
    "description": "Paul Atreides navigates betrayal and prophecy on the desert planet Arrakis, the universe's only source of the spice that extends life and enables interstellar travel. A sweeping epic of politics, ecology, and destiny that redefined space opera."
  },
  {
    "title": "Foundation",
    "author": "Isaac Asimov",
    "description": "Mathematician Hari Seldon uses psychohistory to predict the fall of the Galactic Empire and establishes a foundation to shorten the ensuing dark age. A cornerstone saga of big ideas, societal engineering, and the mathematics of civilization."
  },
  {
    "title": "Neuromancer",
    "author": "William Gibson",
    "description": "A washed-up hacker is hired for a final job in cyberspace, unleashing a conspiracy that blurs the line between human and artificial intelligence. The novel that birthed cyberpunk and predicted the internet age."
  },
  {
    "title": "Childhood's End",
    "author": "Arthur C. Clarke",
    "description": "Mysterious alien overlords usher Earth into a golden age, but their benevolent rule hides a transformative agenda for humanity's future. A haunting meditation on utopia, transcendence, and the price of evolutionary destiny."
  },
  {
    "title": "The Martian",
    "author": "Andy Weir",
    "description": "Stranded astronaut Mark Watney uses ingenuity and humor to survive on Mars after a mission goes catastrophically wrong. A thrilling celebration of science, problem-solving, and the human spirit under extreme pressure."
  },
  {
    "title": "Old Man's War",
    "author": "John Scalzi",
    "description": "Seniors are recruited into a space army with rejuvenated bodies, discovering the brutal realities of interstellar conflict. A sharp, fast-paced take on identity, mortality, and the ethics of war among the stars."
  },
  {
    "title": "Flowers for Algernon",
    "author": "Daniel Keyes",
    "description": "A man with intellectual disabilities undergoes an experimental treatment that dramatically increases his intelligence, only to face heartbreaking consequences. A poignant exploration of intelligence, identity, and what it means to be human."
  },
  {
    "title": "The Time Machine",
    "author": "H.G. Wells",
    "description": "A Victorian inventor journeys to the distant future, encountering the peaceful Eloi and the subterranean Morlocks. A foundational work that critiques class divisions and explores evolution's potential paths."
  },
  {
    "title": "The Andromeda Strain",
    "author": "Michael Crichton",
    "description": "A team of scientists races to contain a deadly extraterrestrial microorganism before it wipes out humanity. A tense techno-thriller that established the template for outbreak narratives."
  },
  {
    "title": "Ready Player One",
    "author": "Ernest Cline",
    "description": "In a dystopian 2044, a teen competes in a virtual reality treasure hunt based on 1980s pop culture. A nostalgic, fast-paced adventure that examines escapism and corporate control in digital worlds."
  },
  {
    "title": "The Hitchhiker's Guide to the Galaxy",
    "author": "Douglas Adams",
    "description": "Arthur Dent survives Earth's destruction and embarks on a absurd journey through space with an alien friend and a guidebook. A beloved satire that mocks bureaucracy, philosophy, and the meaning of life itself."
  },
  {
    "title": "Nineteen Eighty-Four",
    "author": "George Orwell",
    "description": "Winston Smith rebels against a totalitarian regime that controls every aspect of life through surveillance and language manipulation. A chillingly relevant dystopia that defined concepts like Big Brother and doublethink."
  },
  {
    "title": "The Martian Chronicles",
    "author": "Ray Bradbury",
    "description": "Linked stories chronicle humanity's colonization of Mars, exploring themes of nostalgia, colonialism, and environmental destruction. A poetic meditation on what we leave behind when we reach for the stars."
  },
  {
    "title": "The Forever War",
    "author": "Joe Haldeman",
    "description": "Soldiers in an interstellar war experience time dilation, returning to an increasingly alien Earth. A powerful allegory for Vietnam and the disorientation of soldiers returning home to changed societies."
  },
  {
    "title": "Brave New World",
    "author": "Aldous Huxley",
    "description": "In a future of genetic engineering and state-sanctioned happiness, an outsider questions the cost of stability. A prescient dystopia that anticipated antidepressants, consumer culture, and the pursuit of pleasure over truth."
  },
  {
    "title": "Snow Crash",
    "author": "Neal Stephenson",
    "description": "A pizza delivery driver and hacker uncovers a digital drug that affects minds in both virtual and physical reality. A mind-bending cyberpunk epic that explores language, viruses, and the nature of consciousness."
  },
  {
    "title": "Ender's Game",
    "author": "Orson Scott Card",
    "description": "Child prodigy Ender Wiggin trains at a military academy to defend Earth from alien invasion, facing moral dilemmas about manipulation and warfare. A coming-of-age story that questions the ethics of using children as weapons."
  },
  {
    "title": "Leviathan Wakes",
    "author": "James S.A. Corey",
    "description": "A detective and a ship's captain uncover a conspiracy that threatens the fragile peace between Earth, Mars, and the Belt. A space opera that blends noir mystery with political intrigue and cosmic horror."
  },
  {
    "title": "Children of Time",
    "author": "Adrian Tchaikovsky",
    "description": "Humanity's last survivors discover a planet where uplifted spiders have built a civilization, leading to a clash of species and ideologies. A brilliant exploration of evolution, consciousness, and what civilization might become."
  },
  {
    "title": "Project Hail Mary",
    "author": "Andy Weir",
    "description": "An amnesiac astronaut wakes up on a solo mission to save Earth from an extinction-level event. A thrilling tale of scientific discovery and unexpected friendship across species."
  },
  {
    "title": "Hyperion",
    "author": "Dan Simmons",
    "description": "Seven pilgrims share their stories on a journey to confront the mysterious Shrike creature. A Canterbury Tales-inspired space opera that weaves mythology, time travel, and literary allusions into an epic narrative."
  },
  {
    "title": "Ubik",
    "author": "Philip K. Dick",
    "description": "In a world where psychics battle corporations, reality itself begins to unravel for the protagonist. A mind-bending exploration of death, reality, and the nature of existence."
  },
  {
    "title": "Slaughterhouse-Five",
    "author": "Kurt Vonnegut",
    "description": "Billy Pilgrim becomes unstuck in time, experiencing his life out of order during World War II and alien abduction. An anti-war classic that blends science fiction with autobiography and dark humor."
  },
  {
    "title": "A Fire Upon the Deep",
    "author": "Vernor Vinge",
    "description": "A galactic war erupts over a transcendent AI, with a family stranded on a planet with medieval-level aliens. A vast space opera that explores the concept of zones of thought limiting technological advancement."
  },
  {
    "title": "The Dispossessed",
    "author": "Ursula K. Le Guin",
    "description": "A physicist travels between an anarchist moon and its capitalist planet, exploring contrasting social systems. A profound meditation on freedom, responsibility, and the price of utopia."
  },
  {
    "title": "Dune Messiah",
    "author": "Frank Herbert",
    "description": "Emperor Paul Atreides faces religious fanaticism and political machinations as his prescient visions trap him in an inescapable future. A darker sequel that deconstructs the hero's journey and explores the dangers of messianic power."
  },
  {
    "title": "The Dark Forest",
    "author": "Liu Cixin",
    "description": "Humanity must prepare for an alien invasion while dealing with the universe's dark forest theory of survival. A sweeping cosmic thriller that applies game theory to interstellar relations."
  },
  {
    "title": "Seveneves",
    "author": "Neal Stephenson",
    "description": "After the Moon shatters, humanity has two years to save itself from extinction by escaping into space. An epic of survival, genetics, and the rebuilding of civilization over five thousand years."
  },
  {
    "title": "The Moon is a Harsh Mistress",
    "author": "Robert A. Heinlein",
    "description": "A lunar colony revolts against Earth's control, led by a sentient computer and a reluctant revolutionary. A libertarian classic that explores revolution, AI consciousness, and the price of freedom."
  },
  {
    "title": "Solaris",
    "author": "Stanisław Lem",
    "description": "A psychologist studies a planet-sized intelligence that manifests the crew's repressed memories as physical beings. A philosophical exploration of communication with truly alien intelligence and the limits of human understanding."
  },
  {
    "title": "Do Androids Dream of Electric Sheep?",
    "author": "Philip K. Dick",
    "description": "A bounty hunter retires rogue androids in a post-apocalyptic world, questioning what makes someone truly human. The inspiration for Blade Runner, exploring empathy, authenticity, and the nature of consciousness."
  },
  {
    "title": "Ringworld",
    "author": "Larry Niven",
    "description": "Explorers discover a massive artificial ring orbiting a star, with a habitable inner surface larger than a million Earths. A classic of big-idea science fiction that expanded the scale of possible megastructures."
  },
  {
    "title": "Stranger in a Strange Land",
    "author": "Robert A. Heinlein",
    "description": "A human raised by Martians returns to Earth with psychic powers and radical ideas about society, religion, and sexuality. A counterculture classic that challenged 1960s social norms and introduced the concept of 'grokking'."
  },
  {
    "title": "The Left Hand of Darkness",
    "author": "Ursula K. Le Guin",
    "description": "A human envoy navigates the politics of a planet where inhabitants are ambisexual, changing gender monthly. A groundbreaking exploration of gender, friendship, and cultural understanding."
  },
  {
    "title": "Blindsight",
    "author": "Peter Watts",
    "description": "A crew of genetically modified specialists investigates an alien probe, discovering consciousness might be an evolutionary dead end. A hard science fiction thriller that challenges assumptions about intelligence and self-awareness."
  },
  {
    "title": "Ancillary Justice",
    "author": "Ann Leckie",
    "description": "A warship AI trapped in a human body seeks revenge across an interstellar empire. A space opera that explores identity, gender, and the nature of consciousness through the lens of a divided artificial intelligence."
  },
  {
    "title": "Permutation City",
    "author": "Greg Egan",
    "description": "In a future where consciousness can be uploaded to computers, a man creates a self-contained digital universe. A mind-bending exploration of artificial life, consciousness, and the nature of reality itself."
  },
  {
    "title": "Revelation Space",
    "author": "Alastair Reynolds",
    "description": "An archaeologist, assassin, and soldier uncover an ancient threat that has wiped out civilizations for millions of years. A dark space opera that combines cosmic horror with deep time and interstellar mystery."
  },
  {
    "title": "Consider Phlebas",
    "author": "Iain M. Banks",
    "description": "During a galactic war, a shape-shifting spy races to retrieve an AI from a forbidden planet. The first in the Culture series, introducing a post-scarcity society where AI minds oversee human affairs."
  },
  {
    "title": "A Canticle for Leibowitz",
    "author": "Walter M. Miller Jr.",
    "description": "Monks preserve knowledge through centuries of darkness after nuclear war, as humanity repeats its mistakes. A cyclical history that examines the relationship between faith, science, and human nature."
  },
  {
    "title": "The War of the Worlds",
    "author": "H.G. Wells",
    "description": "Martian invaders devastate Earth with advanced technology, only to fall to Earth's smallest defenders. A pioneering alien invasion story that critiques British imperialism and explores evolution's blind spots."
  },
  {
    "title": "I, Robot",
    "author": "Isaac Asimov",
    "description": "Short stories explore the implications of Asimov's Three Laws of Robotics through various paradoxes and edge cases. The foundational work that introduced ethical frameworks for artificial intelligence."
  },
  {
    "title": "Starship Troopers",
    "author": "Robert A. Heinlein",
    "description": "A young soldier fights in an interstellar war against alien arachnids while exploring citizenship and military service. A controversial military science fiction novel that examines duty, sacrifice, and the morality of war."
  },
  {
    "title": "The Diamond Age",
    "author": "Neal Stephenson",
    "description": "A young girl receives an interactive book designed to educate her for success in a nanotechnology-driven future. A coming-of-age story that explores class, education, and the impact of technology on society."
  },
  {
    "title": "Daemon",
    "author": "Daniel Suarez",
    "description": "After a tech mogul's death, his daemon program begins executing a complex plan to reshape society. A techno-thriller that examines the power of autonomous systems and the potential for technology to subvert traditional power structures."
  },
  {
    "title": "Altered Carbon",
    "author": "Richard K. Morgan",
    "description": "In a future where consciousness can be transferred between bodies, a detective investigates a wealthy man's apparent suicide. A cyberpunk noir that explores identity, mortality, and the commodification of human life."
  },
  {
    "title": "The Road",
    "author": "Cormac McCarthy",
    "description": "A father and son journey through a post-apocalyptic wasteland, clinging to their humanity in a world of ash and despair. A harrowing yet tender meditation on love, survival, and maintaining morality when civilization collapses."
  },
  {
    "title": "Wool",
    "author": "Hugh Howey",
    "description": "In an underground silo, a sheriff's investigation into a woman's death uncovers dangerous secrets about their world's true nature. A claustrophobic dystopia that builds tension through its gradual revelation of a society built on lies."
  },
  {
    "title": "The Three-Body Problem",
    "author": "Liu Cixin",
    "description": "During China's Cultural Revolution, a secret military project makes contact with an alien civilization, leading to an impending invasion. A hard science fiction epic that combines Chinese history with cosmic-scale ideas about physics and civilization."
  }
];

// shuffle them and return one book for review.
return books.sort(() => Math.random() - 0.5)[0];

