={
    "template": {
        "id": "01K8NGN0QYA0RX5AT041BP4KF8",
        "composition": "Final 2"
    },
    "fonts": [
      "This Is The Future.ttf",
      "This Is The Future Italic.ttf"
    ],
    "assets": [
        {
            "type": "text",
            "layerName": "var_author",
            "value": "{{ $('Grab one book for review').item.json.author }}"
        },
        {
            "type": "text",
            "layerName": "var_title",
            "value": "{{ $('Grab one book for review').item.json.title }}"
        },
        {
            "type": "data",
            "property": "Effects.Style Selection.Slider",
            "layerName": "Global Control",
            "value": 4
        },
        {
            "type": "function",
            "name": "nx:genai",
            "params": {
              "provider": "fal",
              "key": "${secrets.FAL_KEY}",
              "model": "infinitalk/single-text",
              "type": "video",
              "layerName": "var_video",
              "data": {
                "image_url": "https://nx1-assets-eu.nexrender.com/example/example_cover_book_review.jpg",
                "text_input": "{{ $('Grab one book for review').item.json.description }}",
                "voice": "Eric",
                "prompt": "A young man with a a beard records audio with a microphone. He appears engaged and expressive, suggesting a podcast or voiceover.",
                "num_frames": 721,
                "resolution": "720p",
                "acceleration": "high"
              }
            }
        },
        {
            "type": "data",
            "composition": "Final 2",
            "layerName": "var_video",
            "property": "containingComp.workAreaDuration",
            "expression": "layer.source.duration"
        }
    ]
}
