// Get all campaign data
const campaigns = $input.all();

// Filter out zero-activity campaigns
const activeCampaigns = campaigns
  .map(item => ({
    id: item.json.campaign.id,
    name: item.json.campaign.name,
    status: item.json.campaign.status,
    impressions: parseInt(item.json.metrics.impressions) || 0,
    clicks: parseInt(item.json.metrics.clicks) || 0,
    cost: parseFloat(item.json.metrics.costMicros || 0) / 1000000,
    conversions: parseFloat(item.json.metrics.conversions) || 0,
    avg_cpc: parseFloat(item.json.metrics.averageCpc || 0) / 1000000
  }))
  .filter(c => c.cost > 0 || c.impressions > 0);

// Calculate CTR and CPA
const enrichedCampaigns = activeCampaigns.map(c => ({
  ...c,
  ctr: c.impressions > 0 ? ((c.clicks / c.impressions) * 100).toFixed(2) : '0',
  cpa: c.conversions > 0 ? (c.cost / c.conversions).toFixed(2) : 'N/A'
}));

// Sort by spend
enrichedCampaigns.sort((a, b) => b.cost - a.cost);

// Create summary
const summary = {
  total_campaigns: enrichedCampaigns.length,
  total_spend: enrichedCampaigns.reduce((sum, c) => sum + c.cost, 0).toFixed(2),
  total_clicks: enrichedCampaigns.reduce((sum, c) => sum + c.clicks, 0),
  total_impressions: enrichedCampaigns.reduce((sum, c) => sum + c.impressions, 0),
  total_conversions: enrichedCampaigns.reduce((sum, c) => sum + c.conversions, 0),

  top_campaigns: enrichedCampaigns.slice(0, 10),

  zero_conversions_high_spend: enrichedCampaigns
    .filter(c => c.conversions === 0 && c.cost > 50)
    .slice(0, 5),

  low_ctr_campaigns: enrichedCampaigns
    .filter(c => parseFloat(c.ctr) < 1 && c.impressions > 100)
    .slice(0, 5),

  high_conversion_campaigns: enrichedCampaigns
    .filter(c => c.conversions > 0)
    .sort((a, b) => b.conversions - a.conversions)
    .slice(0, 5)
};

// âœ… Wrap in parent array
return [{
  json: {
    last_week_data: summary
  }
}];

