// ============================================================================
// Content Pillar Response Parser for n8n
// Purpose: Extract AI-generated content pillars and format for Google Sheets
// ============================================================================

// Step 1: Extract the AI response
const aiResponse = items[0].json;

// Step 2: Navigate to the output_text
let responseText;

try {
  // Handle the nested structure: output[0].content[0].text
  const outputArray = aiResponse.output;

  if (!outputArray || !Array.isArray(outputArray) || outputArray.length === 0) {
    throw new Error('Output array is missing or empty');
  }

  const contentArray = outputArray[0].content;

  if (!contentArray || !Array.isArray(contentArray) || contentArray.length === 0) {
    throw new Error('Content array is missing or empty');
  }

  // Find the output_text type
  const textContent = contentArray.find(item => item.type === 'output_text');

  if (!textContent || !textContent.text) {
    throw new Error('output_text not found in content array');
  }

  responseText = textContent.text;

} catch (error) {
  throw new Error(`Failed to extract AI response: ${error.message}`);
}

// Step 3: Clean the JSON string (remove newlines and extra spaces)
const cleanedText = responseText
  .replace(/```json/gi, '')
  .replace(/```/g, '')
  .trim();

// Step 4: Parse the JSON
let parsed;
try {
  parsed = JSON.parse(cleanedText);
} catch (error) {
  throw new Error(`JSON Parse Error: ${error.message}\n\nReceived text: ${cleanedText.substring(0, 500)}`);
}

// Step 5: Validate the parsed structure
if (!parsed.contentPillars || !Array.isArray(parsed.contentPillars)) {
  throw new Error('Invalid AI response: contentPillars array is missing');
}

if (parsed.contentPillars.length === 0) {
  throw new Error('No content pillars were generated');
}

if (!parsed.platforms || !Array.isArray(parsed.platforms)) {
  throw new Error('Invalid AI response: platforms array is missing');
}

// Step 6: Transform pillars into Google Sheets rows
const rows = parsed.contentPillars.map((pillar, index) => {

  // Validate required fields
  if (!pillar.pillar_name) {
    throw new Error(`Pillar at index ${index} is missing pillar_name`);
  }

  if (!pillar.platform) {
    throw new Error(`Pillar "${pillar.pillar_name}" is missing platform`);
  }

  if (pillar['Weight (%)'] === undefined || pillar['Weight (%)'] === null) {
    throw new Error(`Pillar "${pillar.pillar_name}" is missing Weight (%)`);
  }

  return {
    json: {
      // Column A: Pillar Name
      'Pillar Name': pillar.pillar_name,

      // Column B: Primary Purpose
      'Primary Purpose': pillar.description || '',

      // Column C: Platform
      'Platform': pillar.platform,

      // Column D: Weight (%)
      'Weight (%)': pillar['Weight (%)'],

      // Column E: Allowed Formats
      'Allowed Formats': pillar.pillar_type || '',

      // Column F: CTA Allowed
      'CTA Allowed': pillar['CTA Allowed'] || 'Soft'
    }
  };
});

// Step 7: Validate weights sum to 100% per platform
const platformGroups = {};

rows.forEach(row => {
  const platform = row.json.Platform;
  if (!platformGroups[platform]) {
    platformGroups[platform] = [];
  }
  platformGroups[platform].push(row.json['Weight (%)']);
});

// Check each platform's total weight
Object.entries(platformGroups).forEach(([platform, weights]) => {
  const total = weights.reduce((sum, weight) => sum + parseInt(weight), 0);

  if (total !== 100) {
    throw new Error(
      `Weight validation failed for ${platform}: ` +
      `Total = ${total}% (must equal 100%). ` +
      `Weights: ${weights.join(', ')}`
    );
  }
});

// Step 8: Log summary for debugging
console.log('✓ Successfully parsed AI response');
console.log(`✓ Platforms: ${parsed.platforms.join(', ')}`);
console.log(`✓ Total pillars generated: ${rows.length}`);
console.log(`✓ Calculations: ${JSON.stringify(parsed.calculations, null, 2)}`);

Object.entries(platformGroups).forEach(([platform, weights]) => {
  console.log(`✓ ${platform}: ${weights.length} pillars, ${weights.reduce((a, b) => a + b, 0)}% total weight`);
});

// Step 9: Return formatted rows for Google Sheets
return rows;

