// ============================================================================
// Content Calendar Response Parser for n8n
// Maps to exact Google Sheets structure: Sr No, Date, Platform, Pillar ID, 
// Format, Post Intent, Hook Angle, CTA Type, Status
// ============================================================================

const aiResponse = items[0].json;

// Step 1: Extract the AI response
let responseText;

try {
  const outputArray = aiResponse.output;

  if (!outputArray || !Array.isArray(outputArray) || outputArray.length === 0) {
    throw new Error('Output array is missing or empty');
  }

  const contentArray = outputArray[0].content;

  if (!contentArray || !Array.isArray(contentArray) || contentArray.length === 0) {
    throw new Error('Content array is missing or empty');
  }

  const textContent = contentArray.find(item => item.type === 'output_text');

  if (!textContent || !textContent.text) {
    throw new Error('output_text not found in content array');
  }

  responseText = textContent.text;

} catch (error) {
  throw new Error(`Failed to extract AI response: ${error.message}`);
}

// Step 2: Clean the JSON string
const cleanedText = responseText
  .replace(/```json/gi, '')
  .replace(/```/g, '')
  .trim();

// Step 3: Parse the JSON
let parsed;
try {
  parsed = JSON.parse(cleanedText);
} catch (error) {
  throw new Error(`JSON Parse Error: ${error.message}\n\nReceived: ${cleanedText.substring(0, 500)}`);
}

// Step 4: Validate structure
if (!parsed.contentCalendar || !Array.isArray(parsed.contentCalendar)) {
  throw new Error('Invalid AI response: contentCalendar array is missing');
}

if (parsed.contentCalendar.length === 0) {
  throw new Error('No calendar entries were generated');
}

// Step 5: Transform calendar entries into exact Google Sheets format
const rows = parsed.contentCalendar.map((entry, index) => {

  // Validate required fields
  if (!entry.date) {
    throw new Error(`Calendar entry at index ${index} is missing date`);
  }

  if (!entry.pillarID) {
    throw new Error(`Calendar entry for ${entry.date} is missing pillarID`);
  }

  if (!entry.postIntent) {
    throw new Error(`Calendar entry for ${entry.date} is missing postIntent`);
  }

  return {
    json: {
      // Column A: Sr No
      'Sr No': entry.srNo || (index + 1),

      // Column B: Date
      'Date': entry.date,

      // Column C: Platform
      'Platform': entry.platform || '',

      // Column D: Pillar ID
      'Pillar ID': entry.pillarID,

      // Column E: Format
      'Format': entry.format || 'Text Post',

      // Column F: Post Intent
      'Post Intent': entry.postIntent,

      // Column G: Hook Angle
      'Hook Angle': entry.hookAngle || '',

      // Column H: CTA Type
      'CTA Type': entry.ctaType || 'None',

      // Column I: Status
      'Status': entry.status || 'Pending'
    }
  };
});

// Step 6: Validation and logging
console.log('✓ Successfully parsed calendar');
console.log(`✓ Total posts generated: ${rows.length}`);
console.log(`✓ Date range: ${parsed.calendarMetadata?.startDate} to ${parsed.calendarMetadata?.endDate}`);
console.log(`✓ Platform: ${parsed.calendarMetadata?.platform}`);
console.log(`✓ Posts per week: ${parsed.calendarMetadata?.postsPerWeek}`);

if (parsed.pillarDistribution) {
  console.log('\n✓ Pillar Distribution:');
  Object.entries(parsed.pillarDistribution).forEach(([pillar, stats]) => {
    console.log(`  - ${pillar}: ${stats.actualPosts} posts (${stats.actualWeight}% vs ${stats.targetWeight}% target)`);
  });
}

// Step 7: Validate pillar distribution
const pillarCounts = {};
rows.forEach(row => {
  const pillar = row.json['Pillar ID'];
  pillarCounts[pillar] = (pillarCounts[pillar] || 0) + 1;
});

console.log('\n✓ Actual Post Distribution:');
Object.entries(pillarCounts).forEach(([pillar, count]) => {
  const percentage = ((count / rows.length) * 100).toFixed(1);
  console.log(`  - ${pillar}: ${count} posts (${percentage}%)`);
});

// Step 8: Validate CTA spacing (using default cooldown of 5 days)
const ctaCooldownDays = 5; // Default cooldown - adjust as needed
let lastHardCTAIndex = -999;
let ctaViolations = 0;

rows.forEach((row, index) => {
  if (row.json['CTA Type'] === 'Hard') {
    const daysSinceLastCTA = index - lastHardCTAIndex;
    if (daysSinceLastCTA < ctaCooldownDays) {
      ctaViolations++;
      console.warn(`⚠ CTA spacing violation at post ${index + 1}: only ${daysSinceLastCTA} posts since last hard CTA`);
    }
    lastHardCTAIndex = index;
  }
});

if (ctaViolations === 0) {
  console.log('\n✓ CTA cooldown validation passed');
} else {
  console.warn(`\n⚠ Found ${ctaViolations} CTA spacing violations`);
}

// Step 9: Return formatted rows for Google Sheets
return rows;
