// V1.5 - Still WAF-safe: no regex, no <tr parsing.
// Extracts all JSON-LD blocks and enriches with salary + locations.
// Company/url may be missing because those are not always in JSON-LD.

var first = $input.first();
var html = (first && first.json && first.json.results && first.json.results[0]) ? (first.json.results[0].content || '') : '';

var OPEN = '<script type="application/ld+json">';
var CLOSE = '</script>';

var out = [];
var pos = 0;

while (true) {
  var a = html.indexOf(OPEN, pos);
  if (a === -1) break;

  var b = html.indexOf(CLOSE, a);
  if (b === -1) break;

  var jsonText = html.substring(a + OPEN.length, b).trim();
  pos = b + CLOSE.length;

  var data;
  try {
    data = JSON.parse(jsonText);
  } catch (e) {
    continue;
  }

  if (!data || data['@type'] !== 'JobPosting') continue;

  // Salary (safe, no ??, no ?.)
  var salaryValue = (data.baseSalary && data.baseSalary.value) ? data.baseSalary.value : {};
  var minSalary = (salaryValue && salaryValue.minValue !== undefined && salaryValue.minValue !== null) ? salaryValue.minValue : null;
  var maxSalary = (salaryValue && salaryValue.maxValue !== undefined && salaryValue.maxValue !== null) ? salaryValue.maxValue : null;
  var currency = (data.baseSalary && data.baseSalary.currency) ? data.baseSalary.currency : null;

  // Locations
  var locations = [];
  if (data.jobLocation && Array.isArray(data.jobLocation)) {
    for (var i = 0; i < data.jobLocation.length; i++) {
      var loc = data.jobLocation[i];
      var addr = (loc && loc.address) ? loc.address : {};
      var place = addr.addressCountry || addr.addressRegion || addr.addressLocality || null;
      if (place) locations.push(place);
    }
  }

  out.push({
    json: {
      title: data.title || null,
      company: (data.hiringOrganization && data.hiringOrganization.name) ? data.hiringOrganization.name : null,
      datePosted: data.datePosted || null,
      minSalary: minSalary,
      maxSalary: maxSalary,
      currency: currency,
      employmentType: data.employmentType || null,
      locationType: data.jobLocationType || null,
      locations: locations,
      url: data.url || null,
      description: data.description || null
    }
  });
}

return out;

// Safe version (no arrow, no spread, no optional, no nullish)
var candidate = (items && items[0]) ? items[0].json : {};
var out = [];

for (var i = 1; i < items.length; i++) {
  out.push({
    json: {
      candidate: candidate,
      job: items[i].json
    }
  });
}

return out;

// Safe version: no ?. no ?? no spread
var JOB_NODE_NAME = 'Create candidate-job pairs';
var jobs = $items(JOB_NODE_NAME) || [];

function safeParse(s) {
  if (!s) return {};
  try { return JSON.parse(String(s)); } catch (e) { return {}; }
}

var out = [];

for (var i = 0; i < items.length; i++) {
  var analysis = safeParse(items[i].json.output);
  var job = (jobs[i] && jobs[i].json) ? jobs[i].json : {};

  var merged = {};
  for (var k1 in job) merged[k1] = job[k1];
  for (var k2 in analysis) merged[k2] = analysis[k2];

  out.push({ json: merged });
}

return out;

function parseModelOutput(raw) {
  if (!raw) return {};

  var s = String(raw).trim();

  // Remove ```json ... ``` without literal backticks
  // \x60 is backtick
  s = s.replace(/(\x60{3}json|\x60{3})/g, '').trim();

  // If JSON-string wrapped in quotes, try decode
  if ((s.charAt(0) === '"' && s.charAt(s.length - 1) === '"') ||
      (s.charAt(0) === "'" && s.charAt(s.length - 1) === "'")) {
    try {
      s = JSON.parse(s);
    } catch (e) {
      s = s.substring(1, s.length - 1);
    }
  }

  try {
    return JSON.parse(s);
  } catch (e1) {
    try {
      var s2 = s.replace(/\\\"/g, '"').replace(/\\n/g, '\n');
      return JSON.parse(s2);
    } catch (e2) {
      return { _parse_error: 'Could not parse model output', _raw_preview: s.substring(0, 200) };
    }
  }
}

var out = [];
for (var i = 0; i < items.length; i++) {
  var analysis = parseModelOutput(items[i].json.output);

  var merged = {};
  for (var k in items[i].json) merged[k] = items[i].json[k];
  merged.analysis = analysis;

  out.push({ json: merged });
}

return out;

function parseOutput(raw) {
  if (!raw) return {};
  var s = String(raw).trim().replace(/(\x60{3}json|\x60{3})/g, '').trim();

  if ((s.charAt(0) === '"' && s.charAt(s.length - 1) === '"') ||
      (s.charAt(0) === "'" && s.charAt(s.length - 1) === "'")) {
    try { s = JSON.parse(s); } catch (e) {}
  }

  try { return JSON.parse(s); } catch (e1) {
    try { return JSON.parse(s.replace(/\\\"/g, '"').replace(/\\n/g, '\n')); } catch (e2) { return {}; }
  }
}

var out = [];

for (var i = 0; i < items.length; i++) {
  var item = items[i].json;

  var j = item.job ? item.job : item;
  var parsed = parseOutput(item.output);

  function pick(v1, v2, v3, def) {
    if (v1 !== undefined && v1 !== null && v1 !== '') return v1;
    if (v2 !== undefined && v2 !== null && v2 !== '') return v2;
    if (v3 !== undefined && v3 !== null && v3 !== '') return v3;
    return def;
  }

  var skill = pick(item.skill_match_percentage, parsed.skill_match_percentage, null, 0);
  var complexity = pick(item.job_complexity_score, parsed.job_complexity_score, null, 0);
  var fit = pick(item.fit_score, parsed.fit_score, null, 0);
  var finalRec = pick(item.final_recommendation, parsed.final_recommendation, null, '');
  var summary = pick(item.summary, item.alignment_explanation, parsed.alignment_explanation, '');

  var locationsStr = '';
  if (j.locations && Array.isArray(j.locations)) locationsStr = j.locations.join(', ');
  else locationsStr = pick(j.locations, item.locations, null, '');

  out.push({
    json: {
      title: pick(j.title, item.title, null, ''),
      company: pick(j.company, item.company, null, ''),
      url: pick(j.url, item.url, null, ''),
      date_posted: pick(j.datePosted, item.date_posted, null, ''),
      currency: pick(j.currency, item.currency, null, ''),
      min_salary: pick(j.minSalary, item.minSalary, item.min_salary, ''),
      max_salary: pick(j.maxSalary, item.maxSalary, item.max_salary, ''),
      type: pick(j.employmentType, item.employmentType, item.type, ''),
      locations: locationsStr,

      skill_match_percentage: skill,
      job_complexity_score: complexity,
      fit_score: fit,
      final_recommendation: finalRec,
      summary: summary
    }
  });
}

return out;

function esc(s) {
  return String(s || '')
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;');
}

var jobs = [];
for (var i = 0; i < items.length; i++) jobs.push(items[i].json);
jobs = jobs.slice(0, 5);

var rows = [];

for (var idx = 0; idx < jobs.length; idx++) {
  var j = jobs[idx];

  var title = esc(j.title);
  var company = esc(j.company);
  var url = esc(j.url);
  var locations = esc(j.locations);
  var currency = esc(j.currency);

  var min = j.min_salary || '';
  var max = j.max_salary || '';
  var fit = (j.fit_score !== undefined && j.fit_score !== null) ? j.fit_score : '';
  var match = (j.skill_match_percentage !== undefined && j.skill_match_percentage !== null) ? j.skill_match_percentage : '';

  var summaryRaw = j.summary || '';
  var summary = esc(summaryRaw.substring(0, 260)) + (summaryRaw.length > 260 ? '...' : '');

  var salaryPart = '';
  if (currency && (min || max)) salaryPart = ' · ' + currency + ' ' + min + ((min && max) ? '-' : '') + max;

  var bar = Math.min(100, Math.max(0, Number(match) || 0));

  rows.push(
    '<div style="padding:14px 16px; border-top:' + (idx === 0 ? '0' : '1px solid #e5e7eb') + ';">' +
      '<div style="display:flex; justify-content:space-between; gap:14px; align-items:flex-start;">' +
        '<div style="flex:1;">' +
          '<div style="font-size:16px; font-weight:800; margin:0 0 4px;">' + (idx + 1) + '. ' + title + '</div>' +
          '<div style="color:#374151; margin:0 0 10px; font-size:13px;">' +
            '<strong>' + company + '</strong>' +
            (locations ? ' · <span>' + locations + '</span>' : '') +
            salaryPart +
            (fit !== '' ? ' · <span>Fit: ' + esc(fit) + '</span>' : '') +
          '</div>' +
          (summary ? '<div style="color:#4b5563; font-size:13px; margin:0 0 10px;">' + summary + '</div>' : '') +
          '<a href="' + url + '" style="display:inline-block; padding:9px 12px; background:#111827; color:#ffffff; text-decoration:none; border-radius:10px; font-size:13px;">Open job</a>' +
        '</div>' +
        '<div style="min-width:130px; text-align:right;">' +
          '<div style="font-size:12px; color:#6b7280;">Skill match</div>' +
          '<div style="font-size:22px; font-weight:900; margin-top:2px;">' + esc(match) + '%</div>' +
          '<div style="margin-top:8px; height:8px; background:#e5e7eb; border-radius:999px; overflow:hidden;">' +
            '<div style="height:8px; width:' + bar + '%; background:#10b981;"></div>' +
          '</div>' +
        '</div>' +
      '</div>' +
    '</div>'
  );
}

var html =
  '<div style="font-family: Arial, Helvetica, sans-serif; color:#111827; line-height:1.5;">' +
    '<h2 style="margin:0 0 8px;">Your Top Job Matches</h2>' +
    '<p style="margin:0 0 14px; color:#4b5563;">Filter applied: <strong>Fit score &gt;= 40</strong></p>' +
    '<div style="border:1px solid #e5e7eb; border-radius:12px; overflow:hidden;">' +
      (rows.length ? rows.join('') : '<div style="padding:14px 16px; color:#6b7280;">No jobs matched the filter.</div>') +
    '</div>' +
    '<div style="margin-top:14px; font-size:12px; color:#6b7280;">Sent by your n8n Job Matching workflow · ' + new Date().toISOString() + '</div>' +
  '</div>';

return [{ json: { html: html } }];
