var arr = [];
for (var i = 0; i < items.length; i++) {
  arr.push(items[i].json);
}

// Sort by date ascending (recommended)
arr.sort(function(a, b) {
  return String(a.date).localeCompare(String(b.date));
});

return [{ json: { ohlc: arr, ohlc_len: arr.length } }];

// n8n Code node (WAF-safe / ES5)
// Output: 1 item with fundamentals + OHLCV metrics + growth_potential_score
// No modern JS: no =>, no ?. , no ??, no backticks, no spread

function isArray(x) {
  return Object.prototype.toString.call(x) === '[object Array]';
}
function toNum(x) {
  var n = Number(x);
  return (isNaN(n) || !isFinite(n)) ? null : n;
}
function safeStr(x) {
  if (x === undefined || x === null) return '';
  return String(x);
}
function mean(arr) {
  if (!arr || !arr.length) return null;
  var s = 0, c = 0;
  for (var i = 0; i < arr.length; i++) {
    if (arr[i] !== null) { s += arr[i]; c++; }
  }
  return c ? (s / c) : null;
}
function std(arr) {
  if (!arr || arr.length < 2) return null;
  var m = mean(arr);
  if (m === null) return null;
  var s = 0, c = 0;
  for (var i = 0; i < arr.length; i++) {
    if (arr[i] !== null) {
      var d = arr[i] - m;
      s += d * d;
      c++;
    }
  }
  if (c < 2) return null;
  return Math.sqrt(s / (c - 1));
}
function sma(values, period) {
  if (!values || values.length < period) return null;
  var sum = 0;
  for (var i = values.length - period; i < values.length; i++) sum += values[i];
  return sum / period;
}
function rsi(values, period) {
  if (!values || values.length < period + 1) return null;
  var gains = 0;
  var losses = 0;
  for (var i = values.length - period; i < values.length; i++) {
    var ch = values[i] - values[i - 1];
    if (ch >= 0) gains += ch;
    else losses += (-ch);
  }
  var avgGain = gains / period;
  var avgLoss = losses / period;
  if (avgLoss === 0) return 100;
  var rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}
function pct(a, b) {
  if (a === null || b === null || b === 0) return null;
  return ((a / b) - 1) * 100;
}
function clamp(x, a, b) {
  return Math.max(a, Math.min(b, x));
}

// ------------------------------------------------------------
// 1) Find fundamentals object (the one that contains General/Highlights)
// ------------------------------------------------------------
function findFundamentalsObject(allItems) {
  for (var i = 0; i < allItems.length; i++) {
    var j = allItems[i].json;
    if (j && j.General && j.Highlights) return j;
  }
  // sometimes fundamentals nested
  for (var k = 0; k < allItems.length; k++) {
    var jj = allItems[k].json;
    if (jj && jj.fundamentals && jj.fundamentals.General) return jj.fundamentals;
  }
  return null;
}

// ------------------------------------------------------------
// 2) Collect OHLCV candles
//    Supports these cases:
//    A) items[] are candles (each item.json has date/open/high/low/close)
//    B) one item.json has ohlc array: { ohlc: [...] }
// ------------------------------------------------------------
function collectCandles(allItems) {
  // Case B: already aggregated
  for (var i = 0; i < allItems.length; i++) {
    var j = allItems[i].json;
    if (j && j.ohlc && isArray(j.ohlc) && j.ohlc.length) return j.ohlc;
  }

  // Case A: items are candles
  var candles = [];
  for (var k = 0; k < allItems.length; k++) {
    var c = allItems[k].json;
    if (c && c.date !== undefined && c.close !== undefined) {
      candles.push(c);
    }
  }
  if (candles.length) return candles;

  return null;
}

// ------------------------------------------------------------
// 3) Extract fundamentals fields from your confirmed structure
// ------------------------------------------------------------
function extractFundamentals(f) {
  var gen = f.General || {};
  var hi = f.Highlights || {};
  var val = f.Valuation || {};
  var tech = f.Technicals || {};

  var out = {
    ticker: gen.Code || '',
    symbol: gen.PrimaryTicker || '',
    sector: gen.Sector || null,
    industry: gen.Industry || null,

    market_cap: toNum(hi.MarketCapitalization),
    pe: toNum(hi.PERatio),
    forward_pe: toNum(val.ForwardPE),
    eps: toNum(hi.EarningsShare),
    eps_ttm: toNum(hi.DilutedEpsTTM),
    peg: toNum(hi.PEGRatio),

    revenue_ttm: toNum(hi.RevenueTTM),
    gross_profit_ttm: toNum(hi.GrossProfitTTM),

    q_rev_growth_yoy: (hi.QuarterlyRevenueGrowthYOY !== undefined) ? toNum(hi.QuarterlyRevenueGrowthYOY) : null,
    q_eps_growth_yoy: (hi.QuarterlyEarningsGrowthYOY !== undefined) ? toNum(hi.QuarterlyEarningsGrowthYOY) : null,

    beta: toNum(tech.Beta),
    ma_50d: toNum(tech["50DayMA"]),
    ma_200d: toNum(tech["200DayMA"]),
    week52_high: toNum(tech["52WeekHigh"]),
    week52_low: toNum(tech["52WeekLow"])
  };

  return out;
}

// ------------------------------------------------------------
// 4) Compute OHLCV technical metrics
// ------------------------------------------------------------
function computeTechnicals(candles) {
  // sort by date asc
  candles.sort(function(a, b) {
    return safeStr(a.date).localeCompare(safeStr(b.date));
  });

  var closes = [];
  var lows = [];
  var highs = [];
  var vols = [];
  var dates = [];

  for (var i = 0; i < candles.length; i++) {
    var c = toNum(candles[i].close);
    if (c === null) continue;
    closes.push(c);
    lows.push(toNum(candles[i].low));
    highs.push(toNum(candles[i].high));
    vols.push(toNum(candles[i].volume));
    dates.push(safeStr(candles[i].date));
  }

  if (closes.length < 60) {
    return { error: 'Not enough OHLC data', closes_count: closes.length };
  }

  var lastClose = closes[closes.length - 1];
  var lastDate = dates[dates.length - 1];

  // returns
  function closeAt(daysAgo) {
    var idx = closes.length - 1 - daysAgo;
    if (idx < 0) return null;
    return closes[idx];
  }

  var ret30 = pct(lastClose, closeAt(30));
  var ret90 = pct(lastClose, closeAt(90));

  // support/resistance 30
  var start30 = Math.max(0, closes.length - 30);
  var support30 = null;
  var resist30 = null;
  for (var j = start30; j < closes.length; j++) {
    var lo = lows[j];
    var hi = highs[j];
    if (lo !== null) support30 = (support30 === null) ? lo : Math.min(support30, lo);
    if (hi !== null) resist30 = (resist30 === null) ? hi : Math.max(resist30, hi);
  }

  // volatility (annualized %)
  var rets = [];
  for (var k = 1; k < closes.length; k++) {
    rets.push((closes[k] / closes[k - 1]) - 1);
  }
  var volDaily = std(rets);
  var volAnnPct = (volDaily === null) ? null : (volDaily * Math.sqrt(252) * 100);

  // RSI/SMA
  var rsi14 = rsi(closes, 14);
  var sma20 = sma(closes, 20);
  var sma50 = sma(closes, 50);
  var sma200 = sma(closes, 200);

  return {
    date: lastDate,
    last_close: lastClose,
    return_30d_pct: ret30,
    return_90d_pct: ret90,
    volatility_ann_pct: volAnnPct,
    rsi_14: rsi14,
    sma_20: sma20,
    sma_50: sma50,
    sma_200: sma200,
    support_30d: support30,
    resistance_30d: resist30
  };
}

// ------------------------------------------------------------
// 5) Growth potential score (0-100) from fundamentals + technicals
// ------------------------------------------------------------
function growthScore(fund, tech) {
  var score = 50;

  // Growth (YOY)
  if (fund.q_rev_growth_yoy !== null) {
    // 0.18 -> 18 points max 25
    score += clamp(fund.q_rev_growth_yoy * 100, 0, 25);
  }
  if (fund.q_eps_growth_yoy !== null) {
    score += clamp(fund.q_eps_growth_yoy * 100 * 0.5, 0, 12);
  }

  // Valuation penalty
  if (fund.pe !== null) {
    if (fund.pe > 45) score -= 12;
    else if (fund.pe > 35) score -= 7;
    else if (fund.pe < 12) score += 5;
  }

  // Trend
  if (tech.sma_50 !== null && tech.last_close > tech.sma_50) score += 6;
  if (tech.sma_200 !== null && tech.last_close > tech.sma_200) score += 6;
  if (tech.sma_20 !== null && tech.last_close > tech.sma_20) score += 3;

  // RSI sanity
  if (tech.rsi_14 !== null) {
    if (tech.rsi_14 > 75) score -= 5;
    else if (tech.rsi_14 < 25) score -= 2;
  }

  // Volatility penalty
  if (tech.volatility_ann_pct !== null) {
    if (tech.volatility_ann_pct > 45) score -= 10;
    else if (tech.volatility_ann_pct > 30) score -= 5;
  }

  return Math.round(clamp(score, 0, 100));
}

// ------------------------------------------------------------
// MAIN
// ------------------------------------------------------------
var fObj = findFundamentalsObject(items);
var candles = collectCandles(items);

if (!fObj) {
  return [{ json: { error: 'Fundamentals not found in items. Check merge.', hint: 'Make sure the fundamentals item with General/Highlights is included.' } }];
}
if (!candles) {
  return [{ json: { error: 'OHLCV candles not found in items.', hint: 'Make sure OHLC items contain date/close or aggregate as {ohlc:[...]}' } }];
}

var fundOut = extractFundamentals(fObj);
var techOut = computeTechnicals(candles);

if (techOut.error) {
  return [{ json: { error: techOut.error, closes_count: techOut.closes_count || 0, ticker: fundOut.ticker, symbol: fundOut.symbol } }];
}

var gps = growthScore(fundOut, techOut);

// Final payload
return [{
  json: {
    ticker: fundOut.ticker,
    symbol: fundOut.symbol,
    date: techOut.date,
    fundamentals: fundOut,
    technical: techOut,
    growth_potential_score: gps
  }
}];
