// Filter: Only process deals closed in last 7 days to avoid spam
const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

const filtered = items.filter(item => {
  const deal = item.json;
  const closedTime = deal.Closed_Time ? new Date(deal.Closed_Time) : null;
  const modifiedTime = deal.Modified_Time ? new Date(deal.Modified_Time) : null;

  // Use Closed_Time if available, otherwise Modified_Time
  const relevantTime = closedTime || modifiedTime;

  if (!relevantTime) return false;

  // Only process if closed/modified in last 7 days
  return relevantTime >= sevenDaysAgo;
});

// Limit to first 10 deals to prevent overwhelming Slack
return filtered.slice(0, 10).map(item => ({ json: item.json }));

// Get deal from Zoho trigger (first item from filtered deals)
const deal = $item(0).$node["Filter Recent Deals"].json || $item(0).$node["Zoho CRM - Deal Trigger"].json || {};
const created = deal.Created_Time ? new Date(deal.Created_Time) : null;
const closed = deal.Closed_Time ? new Date(deal.Closed_Time) : (deal.Modified_Time ? new Date(deal.Modified_Time) : null);
const totalDays = created && closed ? Math.max(1, Math.ceil((closed - created) / (1000 * 60 * 60 * 24))) : null;

// Historical rows from Google Sheet (expects headers: Created_Time, Closed_Time, Stage, or similar)
const sheetRows = items.map(i => i.json).filter(r => r && (r.Created_Time || r['Created Time'] || r.created_time));

// Normalize column names (handle different formats)
const normalizeRow = (row) => {
  return {
    Created_Time: row.Created_Time || row['Created Time'] || row.created_time || row.CreatedTime,
    Closed_Time: row.Closed_Time || row['Closed Time'] || row.closed_time || row.ClosedTime,
    Stage: row.Stage || row.stage || row.STAGE || row.Status || row.status,
    Deal_Name: row.Deal_Name || row['Deal Name'] || row.deal_name || row.DealName,
    Stage_History: row.Stage_History || row['Stage History'] || row.stage_history || null
  };
};

const rows = sheetRows.map(normalizeRow).filter(r => r.Created_Time && r.Closed_Time);

// Calculate cycle times from historical data
const cycles = rows.map(r => {
  try {
    const c = new Date(r.Created_Time);
    const cl = new Date(r.Closed_Time);
    if (isNaN(c.getTime()) || isNaN(cl.getTime())) return null;
    return Math.max(1, Math.ceil((cl - c) / (1000 * 60 * 60 * 24)));
  } catch (e) {
    return null;
  }
}).filter(c => c !== null);

const avgDays = cycles.length ? +(cycles.reduce((a, b) => a + b, 0) / cycles.length).toFixed(1) : null;
const sortedCycles = [...cycles].sort((a, b) => a - b);
const medianDays = cycles.length ? sortedCycles[Math.floor(sortedCycles.length / 2)] : null;
const p90Days = cycles.length ? sortedCycles[Math.floor(sortedCycles.length * 0.9)] : null;

// Best close day-of-week from historical wins
const winRows = rows.filter(r => {
  const stage = (r.Stage || '').toLowerCase();
  return stage.includes('won') || stage.includes('closed won');
});

const dowCount = winRows.reduce((acc, r) => {
  try {
    const d = new Date(r.Closed_Time);
    if (isNaN(d.getTime())) return acc;
    const key = d.toLocaleDateString('en-US', { weekday: 'short' });
    acc[key] = (acc[key] || 0) + 1;
  } catch (e) {
    // Skip invalid dates
  }
  return acc;
}, {});

const bestCloseDay = Object.entries(dowCount).sort((a, b) => b[1] - a[1])[0]?.[0] || null;

// Stage-dwell analysis (if Stage_History is available as JSON string or comma-separated)
let longestDwellStage = null;
let longestDwellDays = 0;

if (deal.Stage_History || deal['Stage History']) {
  try {
    const history = typeof deal.Stage_History === 'string' ? JSON.parse(deal.Stage_History) : deal.Stage_History;
    if (Array.isArray(history) && history.length > 1) {
      let maxDwell = 0;
      let maxStage = null;

      for (let i = 1; i < history.length; i++) {
        const prev = new Date(history[i-1].timestamp || history[i-1].date);
        const curr = new Date(history[i].timestamp || history[i].date);
        if (!isNaN(prev.getTime()) && !isNaN(curr.getTime())) {
          const dwell = Math.ceil((curr - prev) / (1000 * 60 * 60 * 24));
          if (dwell > maxDwell) {
            maxDwell = dwell;
            maxStage = history[i-1].stage || history[i-1].Stage || 'Unknown';
          }
        }
      }

      longestDwellStage = maxStage;
      longestDwellDays = maxDwell;
    }
  } catch (e) {
    // Stage history not in expected format, skip
  }
}

// Calculate average stage dwell from historical data (if available)
let avgStageDwells = {};
if (rows.some(r => r.Stage_History)) {
  const stageDwells = {};
  rows.forEach(r => {
    try {
      if (r.Stage_History) {
        const history = typeof r.Stage_History === 'string' ? JSON.parse(r.Stage_History) : r.Stage_History;
        if (Array.isArray(history)) {
          for (let i = 1; i < history.length; i++) {
            const stage = history[i-1].stage || history[i-1].Stage || 'Unknown';
            const prev = new Date(history[i-1].timestamp || history[i-1].date);
            const curr = new Date(history[i].timestamp || history[i].date);
            if (!isNaN(prev.getTime()) && !isNaN(curr.getTime())) {
              const dwell = Math.ceil((curr - prev) / (1000 * 60 * 60 * 24));
              if (!stageDwells[stage]) stageDwells[stage] = [];
              stageDwells[stage].push(dwell);
            }
          }
        }
      }
    } catch (e) {
      // Skip invalid history
    }
  });

  Object.keys(stageDwells).forEach(stage => {
    const dwells = stageDwells[stage];
    avgStageDwells[stage] = +(dwells.reduce((a, b) => a + b, 0) / dwells.length).toFixed(1);
  });
}

// Build insights
const insights = [];

if (totalDays && avgDays) {
  if (totalDays > avgDays * 1.25) {
    insights.push(`Slow cycle: ${totalDays}d vs avg ${avgDays}d (+${(totalDays - avgDays).toFixed(1)}d)`);
  } else if (totalDays < avgDays * 0.75) {
    insights.push(`Fast cycle: ${totalDays}d vs avg ${avgDays}d (-${(avgDays - totalDays).toFixed(1)}d)`);
  } else {
    insights.push(`Normal cycle: ${totalDays}d (avg ${avgDays}d)`);
  }
}

if (medianDays) insights.push(`Median historical cycle: ${medianDays}d`);
if (p90Days) insights.push(`90th percentile: ${p90Days}d`);
if (bestCloseDay) insights.push(`Best close day: ${bestCloseDay}`);

if (longestDwellStage && longestDwellDays > 0) {
  insights.push(`Longest stage dwell: ${longestDwellStage} (${longestDwellDays}d)`);

  // Compare to historical average for that stage
  if (avgStageDwells[longestDwellStage]) {
    const avgDwell = avgStageDwells[longestDwellStage];
    if (longestDwellDays > avgDwell * 1.5) {
      insights.push(`${longestDwellStage} took ${longestDwellDays}d (avg ${avgDwell}d) - bottleneck detected!`);
    }
  }
}

// Process improvement suggestions
const suggestions = [];
if (totalDays && avgDays && totalDays > avgDays * 1.25) {
  suggestions.push('Schedule immediate follow-up within 24h to unblock progress');
}
if (longestDwellStage && longestDwellDays > 7) {
  suggestions.push(`Review ${longestDwellStage} stage process - consider early alignment with approval/legal`);
}
if (bestCloseDay) {
  suggestions.push(`Replicate patterns from ${bestCloseDay} closes for future deals`);
}
if (p90Days && totalDays && totalDays > p90Days) {
  suggestions.push('This deal exceeded 90th percentile - conduct post-mortem review');
}
if (suggestions.length === 0) {
  suggestions.push('Continue current process - performance is within expected range');
}

if (!insights.length) insights.push(' No historical insights available - check sheet data format');

return [{ 
  json: { 
    deal, 
    totalDays, 
    avgDays, 
    medianDays, 
    p90Days, 
    bestCloseDay, 
    longestDwellStage,
    longestDwellDays,
    insights,
    suggestions,
    hasData: cycles.length > 0
  } 
}];

