// MERGE all AI outputs before sending to recommendations
// This node aggregates data from parallel AI branches
const mainData = $input.first().json || {};
const sentimentData = $input.item(1)?.json || {};
const predictiveData = $input.item(2)?.json || {};

// Extract AI responses
let sentimentAnalysis = sentimentData.text || sentimentData.sentimentAnalysis || sentimentData;
let predictiveAnalytics = predictiveData.text || predictiveData.predictiveAnalytics || predictiveData;

// Try to parse JSON responses
try {
  if (typeof sentimentAnalysis === 'string') {
    const parsed = JSON.parse(sentimentAnalysis);
    sentimentAnalysis = parsed;
  }
} catch (e) {
  // Keep as string
}

try {
  if (typeof predictiveAnalytics === 'string') {
    const parsed = JSON.parse(predictiveAnalytics);
    predictiveAnalytics = parsed;
  }
} catch (e) {
  // Keep as string
}

// Create merged dataset for AI Smart Recommendations
const mergedData = {
  ...mainData,
  sentimentAnalysis: sentimentAnalysis,
  predictiveAnalytics: predictiveAnalytics,
  // Ensure suggestions array exists for Slack template
  suggestions: mainData.suggestions || []
};

return { json: mergedData };

// Generate advanced data visualization data for charts and trends
const deal = $json.deal || {};
const historical = $json.historicalData || [];

// Prepare data for different chart types
const visualizationData = {
  // Cycle time trend over time
  cycleTimeTrend: historical.map((item, index) => ({
    period: `Period ${index + 1}`,
    cycleTime: item.totalDays || 0,
    dealValue: item.deal?.Amount || 0
  })),

  // Stage distribution
  stageDistribution: historical.reduce((acc, item) => {
    const stage = item.deal?.Stage || 'Unknown';
    acc[stage] = (acc[stage] || 0) + 1;
    return acc;
  }, {}),

  // Performance metrics
  performanceMetrics: {
    avgCycleTime: $json.avgDays || 0,
    currentCycleTime: $json.totalDays || 0,
    winRate: historical.filter(h => h.deal?.Stage?.toLowerCase().includes('won')).length / historical.length * 100,
    totalValue: historical.reduce((sum, h) => sum + (h.deal?.Amount || 0), 0)
  },

  // Risk heatmap data
  riskFactors: {
    sentimentRisk: $json.sentimentAnalysis?.riskLevel || 'Low',
    cycleRisk: $json.totalDays > ($json.avgDays * 1.5) ? 'High' : 'Low',
    predictionRisk: $json.predictiveAnalytics?.winProbability < 50 ? 'High' : 'Low'
  }
};

// Generate chart configurations
const charts = {
  cycleTrendChart: {
    type: 'line',
    data: visualizationData.cycleTimeTrend,
    title: 'Sales Cycle Time Trend'
  },
  stagePieChart: {
    type: 'pie',
    data: Object.entries(visualizationData.stageDistribution).map(([stage, count]) => ({
      label: stage,
      value: count
    })),
    title: 'Deal Distribution by Stage'
  },
  performanceGauge: {
    type: 'gauge',
    data: visualizationData.performanceMetrics.winRate,
    title: 'Win Rate %'
  }
};

return {
  json: {
    ...$json,
    visualizationData,
    charts,
    chartSummary: `Generated ${Object.keys(charts).length} visualizations: cycle trends, stage distribution, and performance metrics`
  }
};
