// Get the JSON string using the exact path provided by the user.
const jsonString = $input.first().json.output.string;

let parsedData;

// Check if the string exists before attempting to parse
if (!jsonString) {
    // Return an empty array or throw an error if no string is found
    // Throwing an error is usually better to stop the workflow if data is missing.
    throw new Error("Input string is empty or missing at the specified path: $input.first().json.output.string");
}

try {
    // 1. Parse the JSON string into a JavaScript array of objects
    parsedData = JSON.parse(jsonString);
} catch (error) {
    // Handle JSON parsing errors (e.g., if the string is malformed)
    throw new Error(`Failed to parse JSON string: ${error.message}`);
}

// 2. Ensure the parsed data is an array
if (!Array.isArray(parsedData)) {
    throw new Error('Parsed data is not an array. It cannot be split into multiple items.');
}

// 3. Map the array of objects into the n8n item format { json: object }
// Each element in this array will be treated as a new item by n8n, achieving the split.
const outputItems = parsedData.map(item => ({
    json: item,
}));

// 4. Return the new array of items
return outputItems;

