// 1. Get the data using the path from your first message
// We access .output directly because your data shows it as an array, not a string inside .string
const rawData = $input.first().json.output;

let parsedData;

// Check if the data exists before proceeding
if (!rawData) {
    // Throw an error to stop the workflow if data is missing
    throw new Error("Input is empty or missing at the specified path: $input.first().json.output");
}

// 2. Handle the data format
// If n8n has already parsed it as an array (standard behavior), we use it directly.
// If it happens to be a string (edge case), we parse it.
if (typeof rawData === 'string') {
    try {
        parsedData = JSON.parse(rawData);
    } catch (error) {
        throw new Error(`Failed to parse JSON string: ${error.message}`);
    }
} else {
    parsedData = rawData;
}

// 3. Ensure the data is an array
if (!Array.isArray(parsedData)) {
    throw new Error('The data at json.output is not an array. It cannot be split into multiple items.');
}

// 4. Map the array of objects into the n8n item format { json: object }
// Each element in this array will be treated as a new item by n8n
