// Extract actuarial assumptions from AI agent output
const assumptions = $input.first().json;
const threshold = $('Workflow Configuration').first().json.premiumThreshold;

// Calculate premium adjustment percentage based on loss ratio
let premiumAdjustment = 0;
if (assumptions.lossRatio > 0.75) {
  premiumAdjustment = (assumptions.lossRatio - 0.75) * 0.5; // Increase premiums
} else if (assumptions.lossRatio < 0.60) {
  premiumAdjustment = (assumptions.lossRatio - 0.60) * 0.3; // Decrease premiums
}

// Apply threshold
if (Math.abs(premiumAdjustment) < threshold) {
  premiumAdjustment = 0;
}

// Calculate new premium rates
const adjustmentData = {
  ...assumptions,
  premiumAdjustmentPercentage: Math.round(premiumAdjustment * 10000) / 100,
  adjustmentReason: premiumAdjustment > 0 ? 'Loss ratio exceeds target' : premiumAdjustment < 0 ? 'Loss ratio below target' : 'No adjustment needed',
  effectiveDate: new Date().toISOString().split('T')[0],
  timestamp: new Date().toISOString()
};

return adjustmentData;

