const item = $input.first();

if (!item.json || !item.json.output) {
  throw new Error("Missing AI output");
}

let raw = item.json.output;

// 1ï¸âƒ£ Remove markdown fences
raw = raw
  .replace(/```json/gi, "")
  .replace(/```/g, "")
  .trim();

// 2ï¸âƒ£ Parse JSON
let parsed;
try {
  parsed = JSON.parse(raw);
} catch (error) {
  throw new Error("Failed to parse AI output as JSON: " + error.message);
}

// 3ï¸âƒ£ Ensure array
if (!Array.isArray(parsed)) {
  throw new Error("Parsed output is not an array");
}

// 4ï¸âƒ£ Normalize BOTH old + new formats
return parsed.map(item => {
  // NEW format (nested content_idea)
  const idea = item.content_idea || item;

  return {
    json: {
      platform:
        idea.platform ||
        idea.best_platform ||
        "Unknown",

      content_format:
        idea.format ||
        idea.content_format ||
        "Post",

      content_hook:
        idea.hook ||
        idea.content_hook ||
        "",

      pain_point:
        idea.pain_point ||
        idea.core_pain_point_addressed ||
        "",

      why_it_resonates:
        idea.reason_resonate ||
        idea.why_this_content_will_resonate ||
        "",

      cta:
        idea.cta ||
        idea.suggested_CTA ||
        ""
    }
  };
});
