// Get the first input item (SerpAPI response)
const data = $input.first().json;

// Extract organic results safely
const results = data.organic_results || [];

// Parse and clean the data
const parsedResults = results.map(item => {
  return {
    title: item.title || "",
    summary: item.snippet || "",
    link: item.link || "",
    date: item.date || "Recent",
    source: item.source || "Hacker News"
  };
});

// Return as n8n items
return parsedResults.map(item => ({
  json: item
}));

const items = $input.all();

// Keywords that indicate noise pages
const blockedTitles = [
  "Show | Hacker News",
  "Show HN Guidelines",
  "Launch HNs",
  "Launch HN Instructions"
];

// Keep only real discussion links (/item?id=)
const filtered = items
  .filter(item => {
    const title = item.json.title || "";
    const link = item.json.link || "";

    // Remove known noise titles
    if (blockedTitles.includes(title)) return false;

    // Keep only real discussion threads
    if (!link.includes("news.ycombinator.com/item")) return false;

    return true;
  })
  .map(item => ({
    json: {
      title: item.json.title,
      summary: item.json.summary,
      link: item.json.link,
      date: item.json.date,
      source: item.json.source
    }
  }));

return filtered;

const items = $input.all();

return items.map(item => {
  let text = item.json.output || "";

  // Remove numbered headings like "1. **Key Signal**"
  text = text.replace(/\d+\.\s*\*\*.*?\*\*\n?/g, "");

  // Remove bullet markers
  text = text.replace(/-\s+/g, "");

  // Remove extra line breaks
  text = text.replace(/\n{2,}/g, "\n\n");

  // Trim whitespace
  text = text.trim();

  return {
    json: {
      relaxed_output: text
    }
  };
});
