// Use a regex to extract the hostname.
// The regex breakdown:
// ^(?:https?:\/\/)?  --> Optionally match the protocol (http:// or https://)
// (?:www\.)?         --> Optionally match "www."
// ([^\/]+)           --> Capture all characters until the first "/" (the hostname)
const match = $input.item.json.url.match(/^(?:https?:\/\/)?(?:www\.)?([^\/]+)/i);

if (!match) {
  throw new Error("Unable to regex match url")
}

const domain = match[1];
let sourceName = domainSourceMap[domain];

if (!sourceName) {
  // Fallback: generate a friendly name by removing any leading "www.", 
  // then removing the TLD (the last dot segment),
  // and finally replacing any remaining dots with dashes.
  sourceName = domain
    .replace(/^www\./, '')
    .replace(/\.[^.]+$/, '') // removes the TLD
    .replace(/\./g, '-');
}

if (!sourceName) {
    throw new Error(`Unknown source detected: ${domain}`);
}

