// Generate comprehensive IT provisioning checklist
const item = $input.first().json;

// Create system access requests
const accessRequests = item.requiredSystems.map(system => ({
    system: system,
    employeeName: item.fullName,
    employeeEmail: item.email,
    employeeId: item.employeeId,
    department: item.department,
    priority: item.isTechRole ? 'High' : 'Medium',
    status: 'Pending',
    requestedAt: new Date().toISOString()
}));

// Create hardware requirements
const hardwareNeeds = [];
if (item.isTechRole) {
    hardwareNeeds.push(
        { item: 'MacBook Pro 16"', quantity: 1, priority: 'Critical' },
        { item: 'External Monitor', quantity: 2, priority: 'High' },
        { item: 'Mechanical Keyboard', quantity: 1, priority: 'Medium' },
        { item: 'Ergonomic Mouse', quantity: 1, priority: 'Medium' },
        { item: 'Laptop Stand', quantity: 1, priority: 'Medium' },
        { item: 'USB-C Hub', quantity: 1, priority: 'High' }
    );
} else if (item.isManager) {
    hardwareNeeds.push(
        { item: 'MacBook Air', quantity: 1, priority: 'Critical' },
        { item: 'External Monitor', quantity: 1, priority: 'High' },
        { item: 'Wireless Mouse', quantity: 1, priority: 'Medium' }
    );
} else {
    hardwareNeeds.push(
        { item: 'Standard Laptop', quantity: 1, priority: 'Critical' },
        { item: 'Wireless Mouse', quantity: 1, priority: 'Low' }
    );
}

// Add desk setup
hardwareNeeds.push(
    { item: 'Ergonomic Chair', quantity: 1, priority: 'High' },
    { item: 'Desk', quantity: 1, priority: 'High' },
    { item: 'Welcome Kit', quantity: 1, priority: 'Medium' }
);

item.itProvisioningTicket = {
    ticketId: `IT-${Date.now().toString().slice(-8)}`,
    accessRequests: accessRequests,
    hardwareNeeds: hardwareNeeds,
    totalItems: accessRequests.length + hardwareNeeds.length,
    estimatedCompletionDays: item.isTechRole ? 3 : 2,
    assignedTo: 'IT Ops Team',
    createdAt: new Date().toISOString()
};

return { json: item };

