parameters.jsCode:
// Enhanced employee data validation and enrichment
const item = $input.first().json.body || $input.first().json;

// Basic validation
if (!item.firstName || !item.lastName || !item.email) {
    throw new Error('Missing required fields: firstName, lastName, or email');
}

// Generate comprehensive employee profile
item.fullName = `${item.firstName} ${item.lastName}`;
item.generatedAt = new Date().toISOString();
item.startDate = item.startDate || new Date().toISOString().split('T')[0];

// Enhanced role classification
const techRoles = ['Engineer', 'Developer', 'IT', 'Data', 'DevOps', 'QA', 'Architect', 'Programmer'];
const managerRoles = ['Manager', 'Director', 'VP', 'Lead', 'Head', 'Chief'];
const salesRoles = ['Sales', 'Account Executive', 'BDR', 'SDR', 'Account Manager'];
const financeRoles = ['Finance', 'Accounting', 'Controller', 'Analyst'];

item.isTechRole = techRoles.some(role => item.jobTitle?.includes(role));
item.isManager = managerRoles.some(role => item.jobTitle?.includes(role));
item.isSales = salesRoles.some(role => item.jobTitle?.includes(role));
item.isFinance = financeRoles.some(role => item.jobTitle?.includes(role));

// Determine department
if (item.isTechRole) item.department = 'Engineering';
else if (item.isSales) item.department = 'Sales';
else if (item.isFinance) item.department = 'Finance';
else if (item.isManager) item.department = 'Management';
else item.department = item.department || 'General';

// Generate employee ID if not provided
item.employeeId = item.employeeId || `EMP-${Date.now().toString().slice(-6)}`;

// Determine required systems access
item.requiredSystems = [];
if (item.isTechRole) {
    item.requiredSystems.push('GitHub', 'AWS', 'Jira', 'Confluence', 'VPN');
}
if (item.isSales) {
    item.requiredSystems.push('Salesforce', 'HubSpot', 'LinkedIn Sales Navigator');
}
if (item.isFinance) {
    item.requiredSystems.push('QuickBooks', 'Expensify', 'Bill.com');
}
if (item.isManager) {
    item.requiredSystems.push('BambooHR', 'Workday', 'Performance Management System');
}
// Everyone gets basic systems
item.requiredSystems.push('Slack', 'Google Workspace', '1Password');

// Calculate onboarding checklist size
item.checklistItems = 5 + (item.isTechRole ? 3 : 0) + (item.isManager ? 2 : 0);

// Set welcome package type
item.packageType = item.isManager ? 'Leadership' : (item.isTechRole ? 'Technical' : 'Standard');

return { json: item };
