// Get all merged items from RSS feeds
const allItems = items;

// Create a Set to track unique article URLs/GUIDs
const seenUrls = new Set();
const uniqueArticles = [];

// Filter duplicates based on GUID or link
for (const item of allItems) {
  const identifier = item.json.guid || item.json.link;

  if (!seenUrls.has(identifier)) {
    seenUrls.add(identifier);
    uniqueArticles.push(item);
  }
}

console.log(`Total articles: ${allItems.length}`);
console.log(`Unique articles: ${uniqueArticles.length}`);
console.log(`Duplicates removed: ${allItems.length - uniqueArticles.length}`);

return uniqueArticles;

// Get existing Notion articles
const notionItems = $("Get Existing Articles").all() || [];

console.log('=== FILTER NEW ARTICLES DEBUG ===');
console.log('Total items in Notion:', notionItems.length);

// Extract existing URLs from Notion - try different possible locations
const existingUrls = [];

for (const item of notionItems) {
  let url = null;

  // Try different possible URL locations in Notion response
  if (item.json.properties?.URL?.url) {
    url = item.json.properties.URL.url;
  } else if (item.json.properties?.URL?.rich_text?.[0]?.plain_text) {
    url = item.json.properties.URL.rich_text[0].plain_text;
  } else if (item.json.property_url) {
    url = item.json.property_url;
  } else if (item.json.url) {
    url = item.json.url;
  }

  if (url) {
    existingUrls.push(url);
  }
}

console.log('URLs extracted from Notion:', existingUrls.length);
console.log('First 3 URLs:', existingUrls.slice(0, 3));

// Get all RSS articles
const allRssItems = $("Remove Duplicates").all();
console.log('Total unique RSS articles:', allRssItems.length);

// Filter only new articles
const newArticles = allRssItems.filter(item => {
  const articleUrl = item.json.guid || item.json.link;
  const isNew = !existingUrls.includes(articleUrl);

  if (!isNew) {
    console.log('DUPLICATE FOUND:', articleUrl);
  }

  return isNew;
});

console.log('New articles found:', newArticles.length);
console.log('Duplicates filtered:', allRssItems.length - newArticles.length);

if (newArticles.length > 0) {
  return newArticles;
} else {
  console.log('No new articles to process');
  return [];
}

