// Process ALL items from AI Content Analysis
const results = [];

for (let i = 0; i < items.length; i++) {
  const aiResponse = items[i].json;

  // Parse AI response
  let aiAnalysis;
  try {
    const content = aiResponse.choices[0].message.content;

    // Try to extract JSON from markdown code blocks if present
    const jsonMatch = content.match(/```json\s*([\s\S]*?)```/) || content.match(/({[\s\S]*})/);
    const jsonStr = jsonMatch ? jsonMatch[1] : content;

    aiAnalysis = JSON.parse(jsonStr.trim());
  } catch (error) {
    console.error('Error parsing AI response for item', i, ':', error);
    // Fallback values if parsing fails
    aiAnalysis = {
      summary: 'AI analysis failed',
      category: 'Other',
      tags: ['uncategorized'],
      sentiment: 'Neutral',
      priority_score: 50
    };
  }

  // Get original article data from the same index in Filter New Articles
  const filterNewArticles = $("Filter New Articles").all();
  const article = filterNewArticles[i].json;

  // Combine all data
  results.push({
    json: {
      title: article.title,
      url: article.guid || article.link,
      summary: aiAnalysis.summary,
      category: aiAnalysis.category,
      tags: Array.isArray(aiAnalysis.tags) ? aiAnalysis.tags.join(', ') : aiAnalysis.tags,
      sentiment: aiAnalysis.sentiment,
      priority_score: parseInt(aiAnalysis.priority_score) || 50,
      source: article.creator || article['dc:creator'] || 'Unknown',
      published_date: article.pubDate || article.isoDate || new Date().toISOString(),
      original_content: article.contentSnippet || article.description || ''
    }
  });
}

console.log('Total items processed:', results.length);
return results;

