// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let data = $input.all().map(item => ({
  "channel_name": item.json.name,
  "channel_id": item.json.id
}));

// Remove duplicates based on channel_id
return data.filter((item, index, self) => 
  index === self.findIndex(t => t.channel_id === item.channel_id)
);

/**
 * Deduplication + Trusted Domain Filter
 */
const data = $getWorkflowStaticData('global');

if (!data.processed) {
  data.processed = {};
}

// ---------------- DUPLICATION CHECK ----------------
const headers = $json.headers || {};
const messageId =
  headers['message-id']

if (data.processed[messageId]) {
  return {}; // ‚õî duplicate inbound email
}

data.processed[messageId] = true;

// ---------------- DOMAIN CHECK ----------------
const disposableDomains = [
  "yopmail.com",
  "mailinator.com",
  "guerrillamail.com",
  "tempmail.com",
  "10minutemail.com",
  "getnada.com",
  "trashmail.com",
  "dispostable.com"
];

const freeEmailDomains = [
  "gmail.com",
  "yahoo.com",
  "outlook.com",
  "hotmail.com",
  "icloud.com"
];

const fromEmail = ($json.from?.email || "").toLowerCase();
const domain = fromEmail.split("@")[1] || "";

// Block disposable domains
if (disposableDomains.includes(domain)) {
  return {};
}

// Block free email providers (optional)
if (freeEmailDomains.includes(domain)) {
  return {};
}

// ‚úÖ Passed all checks
return $input.item;

