const category = $('Merge Category').first().json.output.category;
const channels = $input.all() || [];
let matchedChannel = null;

for (const ch of channels) {
  if (
    ch.json.channel_name.includes(category) ||
    category.includes(ch.json.channel_name)
  ) {
    matchedChannel = ch;
    break;
  }
}

// Return based on whether a match was found
if (matchedChannel) {
  return [
    {
      json: {
        found: true,
        matchedChannelName: matchedChannel.json.channel_name,
        matchedChannelId: matchedChannel.json.channel_id
      }
    }
  ];
} else {
  return [
    {
      json: {
        found: false,
        matchedChannelName: null,
        matchedChannelId: null
      }
    }
  ];
}
