// =============================================
// 2) Prep Lead (Normalize + Lead ID + Funnel Stage)
// =============================================
// Funnel stages:
// - NEW, CONTACTED, QUALIFIED, CLOSED_LOST
//
// This node:
// - generates a safe lead_id
// - normalizes phone into 62xxxx
// - sets initial stage = NEW

function normalizePhone(input) {
  let p = String(input || '').trim();
  p = p.replace(/\s+/g, '');
  p = p.replace(/[^0-9+]/g, '');

  if (p.startsWith('+62')) p = '62' + p.slice(3);
  if (p.startsWith('0')) p = '62' + p.slice(1);
  if (p.startsWith('62')) return p;
  if (/^\d+$/.test(p)) return '62' + p;
  return p;
}

function makeLeadId() {
  const ts = Date.now().toString(36);
  const rnd = Math.random().toString(36).slice(2, 8);
  return `L-${ts}-${rnd}`.toUpperCase();
}

const now = new Date().toISOString();

return [{
  json: {
    lead_id: makeLeadId(),
    name: String($json.Name || '').trim(),
    phone: normalizePhone($json.Phone),

    // Funnel stage
    stage: 'NEW',

    // Routing state
    assigned_sales: '',
    sales_slack_id: '',
    route_count: 0,

    // Timestamps
    created_at: now,
    initial_assigned_at: '',
    assigned_at: '',
    last_routed_at: ''
  }
}];

// =============================================
// 4) Build Sales List (1 object)
// =============================================
// Output: sales_list[] is used for initial assignment and re-routing.

const salesList = items
  .map(i => i.json)
  .filter(r => {
    const a = String(r.active || '').toUpperCase();
    return a === 'ON' || a === 'TRUE' || r.active === true;
  })
  .map(r => ({
    name: String(r.name || '').trim(),
    email: String(r.email || '').trim(),
    slack_id: String(r.slack_id || '').trim(),
    active: true
  }))
  .filter(r => r.name && r.slack_id);

if (!salesList.length) {
  throw new Error('No active sales found. Check sales sheet: name, slack_id, active=ON');
}

return [{ json: { sales_list: salesList, sales_count: salesList.length } }];

// 6) Initial Assign (Round Robin)
// Read the lead directly from node 2 (to avoid being overwritten by Google Sheets nodes)

const lead = $items('2) Prep Lead')[0].json;              // ‚úÖ original lead
const sales = $items('4) Build Sales List')[0].json.sales_list;

// routing_state is typically from node 5 (key/value)
const rs = $items('5) Get routing_state (last_index)')[0]?.json || {};
const lastIndex = Number(rs.value ?? -1);

const nextIndex = (lastIndex + 1) % sales.length;
const assigned = sales[nextIndex];

const now = new Date().toISOString();

return [{
  json: {
    ...lead,

    assigned_sales: assigned.name,
    sales_slack_id: assigned.slack_id,

    assigned_sales_index: nextIndex,
    routing_key: 'last_index',

    route_count: Number(lead.route_count || 0) + 1,

    initial_assigned_at: lead.initial_assigned_at || now,
    assigned_at: now,
    last_routed_at: now,
  }
}];

// Reuse the same builder logic for the SLA path
const salesList = items
  .map(i => i.json)
  .filter(r => {
    const a = String(r.active || '').toUpperCase();
    return a === 'ON' || a === 'TRUE' || r.active === true;
  })
  .map(r => ({
    name: String(r.name || '').trim(),
    email: String(r.email || '').trim(),
    slack_id: String(r.slack_id || '').trim(),
    active: true
  }))
  .filter(r => r.name && r.slack_id);

if (!salesList.length) throw new Error('No active sales found');

return [{ json: { sales_list: salesList, sales_count: salesList.length } }];

// SLA) Hourly Re-route (per item) - n8n 2.1.4 compatible

const lead = $json;
const sales = $items('SLA) Build Sales List')[0].json.sales_list;

if (!Array.isArray(sales) || sales.length === 0) {
  throw new Error("Sales list is empty. Check output of node 'SLA) Build Sales List'.");
}

const currentSlack = String(lead.sales_slack_id || '').trim();
const currentIndex = sales.findIndex(s => String(s.slack_id) === currentSlack);
const safeIndex = currentIndex === -1 ? 0 : currentIndex;

const nextIndex = (safeIndex + 1) % sales.length;
const nextSales = sales[nextIndex];

const now = new Date().toISOString();

// IMPORTANT: For 'Run Once for Each Item', return MUST be an object, not an array
return {
  json: {
    ...lead,
    assigned_sales: nextSales.name,
    sales_slack_id: nextSales.slack_id,
    route_count: Number(lead.route_count || 0) + 1,
    assigned_at: now,
    last_routed_at: now,
  }
};

