// =============================================
// STAGE) Prep Payload (Slack Interactive - Block Actions)
// =============================================
// Slack interactive payload arrives as x-www-form-urlencoded:
// $json.body.payload is a STRING containing JSON

const payloadStr = $json?.body?.payload;

if (!payloadStr) {
  throw new Error('Missing body.payload from Slack (expected x-www-form-urlencoded payload)');
}

let slack;
try {
  slack = JSON.parse(payloadStr);
} catch (e) {
  throw new Error('Invalid JSON in body.payload');
}

// Extract lead_id from button value
const lead_id = String(slack?.actions?.[0]?.value || '').trim();

// The user who clicked
const actor_slack_id = String(slack?.user?.id || '').trim();

// response_url for direct message feedback (optional but recommended)
const response_url = String(slack?.response_url || '').trim();

// Target stage to set (this button is for CONTACTED)
const stage = 'CONTACTED';

if (!lead_id) throw new Error('lead_id required (expected slack.actions[0].value)');
if (!actor_slack_id) throw new Error('actor_slack_id missing (expected slack.user.id)');

const now = new Date().toISOString();

return [{
  json: {
    lead_id,
    stage,
    contacted_at: now,
    contacted_by: actor_slack_id,
    actor_slack_id,
    response_url,     // ‚úÖ needed for "Not allowed" feedback
  }
}];

