// =============================================
// STAGE) Authorization + Idempotency Gate
// - Only the assigned sales can mark CONTACTED
// - Only allowed if current stage === 'NEW'
// =============================================

const payload = $items('STAGE) Prep Payload')[0].json;

// Lookup result: item from node "STAGE) Lookup lead by lead_id"
const leadRow = items?.[0]?.json;

if (!leadRow) {
  return [{
    json: {
      ...payload,
      authorized_owner: false,
      stage_allowed: false,
      authorized: false,
      reason: 'LEAD_NOT_FOUND',
      assigned_sales_slack_id: '',
      current_stage: '',
      lead_name: '',
      lead_phone: '',
    }
  }];
}

const assigned = String(leadRow.sales_slack_id || '').trim();
const actor = String(payload.actor_slack_id || payload.contacted_by || '').trim();
const currentStage = String(leadRow.stage || '').trim().toUpperCase();

// Gate #1: owner check
const authorizedOwner = assigned && actor && (assigned === actor);

// Gate #2: stage check (idempotent)
const stageAllowed = (currentStage === 'NEW');

// Final allow
const authorized = Boolean(authorizedOwner && stageAllowed);

// Reason priority
let reason = '';
if (!authorizedOwner) reason = 'NOT_ASSIGNED_OWNER';
else if (!stageAllowed) reason = 'ALREADY_NOT_NEW';

return [{
  json: {
    ...payload,

    // Flags for the IF node
    authorized_owner: authorizedOwner,
    stage_allowed: stageAllowed,
    authorized,

    reason,

    assigned_sales_slack_id: assigned,
    current_stage: currentStage,

    // Include lead data for better feedback
    lead_name: String(leadRow.name || '').trim(),
    lead_phone: String(leadRow.phone || '').trim(),
  }
}];

