// Read the lead from the previous node in the escalation path (must have lead_id)
const lead = $json;

// Lookup results from ESC) Get escalation flag
const rows = $items("ESC) Get escalation flag").map(i => i.json);

// Consider it found only if there is a non-empty key/value
const found = rows.some(r => r && String(r.key || "").trim() !== "");

return [{
  json: {
    ...lead,
    esc_key: `escalated_${lead.lead_id}`,
    already_escalated: found,
    esc_row: found ? rows.find(r => String(r.key || "").trim() !== "") : null
  }
}];
