// Code node to format Attio People data
const items = $input.all();

return items.map(item => {
  const data = item.json;
  const person = data.person || {};

  // Build the values object
  const values = {};

  // Name (required, array format)
  if (data.name) {
    values.name = [data.name];
  }

  // Job title (from role field)
  if (data.role) {
    values.job_title = [data.role];
  }

  // LinkedIn URL - only include if valid
  if (person.linkedin_url && person.linkedin_url !== 'null' && person.linkedin_url.includes('linkedin.com')) {
    const linkedinUrl = person.linkedin_url.replace('http://', 'https://');
    values.linkedin = [linkedinUrl];
  }

  // Email addresses (skip if null or empty, trim whitespace)
  if (person.email && person.email.trim()) {
    values.email_addresses = [{
      email_address: person.email.trim()
    }];
  }

  return {
    json: {
      data: {
        values: values
      }
    }
  };
});

// Code node to format for Attio

const items = $input.all();



return items.map(item => {

  const org = item.json;



  // Build the values object

  const values = {

    name: [org.name || ''],

    description: [org.short_description || ''],

    linkedin: [org.linkedin_url || '']

  };



  // Only add domains if primary_domain exists and is not empty

  if (org.primary_domain && org.primary_domain.trim() !== '') {

    values.domains = [{

      domain: org.primary_domain

    }];

  }



  return {

    json: {

      data: {

        values: values

      }

    }

  };

});

