// Code node to format insights for Attio

const items = $input.all();

const primaryDomain = $('SetFields').item.json.primary_domain;



return items.map(item => {

  const output = item.json.output;



  // Build the values object

  const values = {

    positioning: [output.positioning || ''],

    conversation_points: [output.conversation_points || ''],

    leadership_conversations: [output.leadership_conversations || ''],

    enrichment_status: ['complete' ||'']

  };



  // Only add domains if primary_domain exists and is not empty

  if (primaryDomain && primaryDomain.trim() !== '') {

    values.domains = [{

      domain: primaryDomain

    }];

  }



  return {

    json: {

      data: {

        values: values

      }

    }

  };

});
