const uploadItems = $input.all();
const originalData = $("Get Ready Task").first().json;
const aiAgent = $("Image Prompt Agent").first().json;

// Ambil semua URL dan fix formatnya
const urls = uploadItems
  .filter(item => item.json?.data?.url)
  .map(item => {
    let url = item.json.data.url;
    url = url.replace('tmpfiles.org/', 'tmpfiles.org/dl/');
    url = url.replace('http://', 'https://');
    return url;
  });

// Validasi: pastikan ada 3 URL
if (urls.length !== 3) {
  throw new Error(`Expected 3 images, got ${urls.length}`);
}

// Ambil prompt dari AI Agent
let prompt = aiAgent.output || aiAgent.text || aiAgent.content || '';

// Fallback jika prompt kosong
if (!prompt || prompt.trim() === '') {
  prompt = `Selfie photo of Indonesian woman holding ${originalData["Product Name"]} naturally in bright room by window. Natural smile, looking at camera. Soft morning light. Casual style. Authentic UGC feel.`;
}

// Limit prompt ke 80 kata
const words = prompt.trim().split(/\s+/);
if (words.length > 80) {
  prompt = words.slice(0, 80).join(' ');
}

// Return request body
return [{
  json: {
    images: urls,
    prompt: prompt,
    metadata: {
      taskId: originalData["Task ID"],
      productId: originalData["Product ID"],
      rowNumber: originalData["row_number"],
      productName: originalData["Product Name"]
    }
  }
}];

