// Separate items with 'data' and 'urls'
const dataItems = items.filter(item => item.json.data !== undefined && item.json.data !== null);
const urlItems = items.filter(item => $('Array into Separate Items').item.json.urls !== undefined && $('Array into Separate Items').item.json.urls !== null);

// Output a single item summarizing presence for the If node
return [
  {
    json: {
      hasData: dataItems.length > 0,
      dataItems,  // keep all 'data' objects
      urlItems    // keep all 'urls' objects
    },
    pairedItem: 0
  }
];

// get lead website
const website = $('Person info').item.json.Website;

// build deduped URL list
const seen = new Set();
const links = [];

for (const item of items) {
  const link = item.json?.links?.trim();
  if (!link) continue;
  if (seen.has(link)) continue;

  seen.add(link);
  links.push(link);
}

// return a single combined result, keeping item linking intact
return [
  {
    json: {
      website,
      links   // <--- renamed from urls to links
    },
    pairedItem: 0
  }
];

// ------------------------------------------------------
// CONFIG
// ------------------------------------------------------
const keywords = [
  "home","index","welcome","research","about","about-us","aboutus","our-story","story","company","team","who-we-are","mission","vision","values",
  "services","service","what-we-do","solutions","offerings","capabilities","expertise","products",
  "testimonials","reviews","case-studies","case-study","success-stories","portfolio","work","results","clients",
  "blog","news","articles","posts","insights","resources","learn",
  "contact","contact-us","get-in-touch","support","help"
];

const blacklist = [
  "privacy","terms","cookie","sitemap","login","signup","account","admin","wp-admin","cpanel",
  "cart","checkout","shop","facebook","instagram","linkedin","twitter","tiktok","youtube"
];

const maxUrls = 5;

// ------------------------------------------------------
// DOMAIN PARSING
// ------------------------------------------------------
let originalUrl = $('Person info').item.json.Website;
let baseOrigin = null;
let baseDomain = null;

try {
  const parsed = new URL(originalUrl);
  baseOrigin = parsed.origin;
  baseDomain = parsed.hostname;
} catch {
  baseOrigin = null;
  baseDomain = null;
}

// ------------------------------------------------------
// NORMALIZE FUNCTION
// ------------------------------------------------------
function normalize(url) {
  if (!url || typeof url !== "string") return null;
  url = url.trim();
  if (url.startsWith("#")) return null;
  if (url.startsWith("mailto:") || url.startsWith("tel:")) return null;
  if (url.startsWith("http://") || url.startsWith("https://")) return url;
  if (baseOrigin) {
    try { return new URL(url, baseOrigin).href; } 
    catch { return null; }
  }
  return null;
}

// ------------------------------------------------------
// FLATTEN + NORMALIZE LINKS
// ------------------------------------------------------
const allLinks = items
  .flatMap(i => i.json.links || [])    // flatten arrays
  .map(l => normalize(l.url || l))     // normalize URLs
  .filter(l => l)                      // remove nulls
  .map(l => ({ url: l }));             // wrap as objects

if (allLinks.length === 0) {
  return [{ json: { url: originalUrl } }];
}

// ------------------------------------------------------
// PRIMARY FILTERING
// ------------------------------------------------------
const primary = allLinks.filter(obj => {
  const url = obj.url.toLowerCase();
  return keywords.some(k => url.includes(k));
});

// ------------------------------------------------------
// FALLBACK FILTERING
// ------------------------------------------------------
const fallback = allLinks.filter(obj => {
  const url = obj.url.toLowerCase();
  if (url.startsWith("mailto:") || url.startsWith("tel:")) return false;
  if (blacklist.some(b => url.includes(b))) return false;
  if (baseDomain) {
    try {
      const domain = new URL(obj.url).hostname;
      if (domain !== baseDomain) return false;
    } catch { return false; }
  }
  return true;
});

// Sort fallback by URL length (longer = more specific)
fallback.sort((a, b) => b.url.length - a.url.length);

// ------------------------------------------------------
// COMBINE RESULTS
// ------------------------------------------------------
const results = [];
const addUnique = url => {
  if (!url) return;
  const clean = url.toLowerCase().trim();
  if (!results.includes(clean)) results.push(clean);
};

// 1. Homepage first
addUnique(originalUrl);

// 2. Include **one primary page** if exists
if (primary.length > 0) addUnique(primary[0].url);

// 3. Fill remaining slots with primary pages (skip the first already added)
primary.slice(1).forEach(l => addUnique(l.url));

// 4. Fill remaining slots with fallback pages
fallback.forEach(l => addUnique(l.url));

// Limit to maxUrls
const final = results.slice(0, maxUrls);

// Limit to maxUrls
const finalUrls = results.slice(0, maxUrls);

// Get the lead's first name
const firstName = $('Person info').item.json['First Name'];

// Get the leads's id
const id = $('Person info').item.json.id;

// Return as a single item with pairing to the current input item
return [
  {
    json: {
      id,
      firstName,
      urls: finalUrls
    },
    pairedItem: 0 // paired to the current input item (index 0)
  }
];
