// Recupera il contenuto HTML dall'input
const htmlContent = $input.item.json.html;

// Converte l'HTML in buffer
const buffer = Buffer.from(htmlContent, 'utf-8');

// Restituisce l'item con il file binario
return {
  json: {},
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'text/html',
      fileName: 'page.html',
      fileExtension: 'html'
    }
  }
};

const allReviews = [];

for (const item of $input.all()) {
  const data = item.json;


  if (data.result && Array.isArray(data.result)) {

    for (const page of data.result) {

      const reviews = page.result?.recensioni || page.result?.reviews;

      if (reviews && Array.isArray(reviews)) {
        reviews.forEach(review => {
          allReviews.push(review);
        });
      }
    }
  }
}


return allReviews.map(review => ({
  json: review
}));
