parameters.jsonBody:
={
  "statement": "MERGE (v:Video {videoId: $videoId}) SET v.title = $title, v.url = $url, v.publishedAt = $publishedAt, v.viewCount = $viewCount, v.duration = $duration, v.channelName = $channelName WITH v UNWIND $topics AS topicName MERGE (t:Topic {name: topicName}) MERGE (v)-[:COVERS]->(t) WITH v UNWIND $tools AS toolName MERGE (tool:Tool {name: toolName}) MERGE (v)-[:USES]->(tool) WITH v UNWIND $concepts AS conceptName MERGE (c:Concept {name: conceptName}) MERGE (v)-[:EXPLAINS]->(c)",
  "parameters": {
    "videoId": "{{ $('Scraping of each URLs').item.json.id }}",
    "title": "{{ $('Scraping of each URLs').item.json.title }}",
    "url": "{{ $('Scraping of each URLs').item.json.url }}",
    "publishedAt": "{{ $('Scraping of each URLs').item.json.date }}",
    "viewCount": {{ $('Scraping of each URLs').item.json.viewCount }},
    "duration": "{{ $('Scraping of each URLs').item.json.duration }}",
    "channelName": "{{ $('Scraping of each URLs').item.json.channelName }}",
    "topics": {{ JSON.stringify($json.output.topics) }},
    "tools": {{ JSON.stringify($json.output.tools) }},
    "concepts": {{ JSON.stringify($json.output.concepts) }}
  }
}
