// Name des Feldes anpassen
const inputFieldName = 'text'; 

for (const item of items) {

    // --- SICHERHEITS-CHECK START ---
    // Wir stellen sicher, dass die Struktur immer existiert, auch wenn sie leer ist.
    // So schlagen Referenzen wie item.json.info.Custom sp√§ter nicht fehl.

    // 1. info.Custom initialisieren
    if (!item.json.info) item.json.info = {};
    if (!item.json.info.Custom) item.json.info.Custom = {}; // Leeres Objekt {} falls fehlend

    // 2. metadata initialisieren
    if (!item.json.metadata) item.json.metadata = {}; // Leeres Objekt {} falls fehlend

    // 3. Default f√ºr text_cleaned
    let content = '';
    // --- SICHERHEITS-CHECK ENDE ---


    // Pr√ºfen, ob wir √ºberhaupt Text zum Bearbeiten haben
    if (item.json[inputFieldName]) {
        content = item.json[inputFieldName];

        // 1. W√∂rter, bei denen wir abschneiden wollen (M√ºll)
        const cutOffWords = [
            'References',
            'Bibliography',
            'Works Cited',
            'LITERATURVERZEICHNIS' 
        ];

        // 2. W√∂rter, die wir UNBEDINGT behalten wollen (Gold)
        const protectedWords = [
            'Appendix',
            'Appendices',
            'Anhang'
        ];

        // Wir suchen das fr√ºheste Vorkommen eines Cut-Off Wortes
        let cutIndex = -1;
        let foundWord = '';

        for (const word of cutOffWords) {
            // Regex: Zeilenumbruch + optional Leerzeichen + Wort (Case Insensitive)
            const regex = new RegExp(`\\n\\s*${word}`, 'i');
            const match = content.match(regex);

            if (match) {
                // Wenn wir was finden, merken wir uns, wo es anf√§ngt
                if (cutIndex === -1 || match.index < cutIndex) {
                    cutIndex = match.index;
                    foundWord = word;
                }
            }
        }

        // JETZT KOMMT DIE MAGIE: Der Sicherheits-Check üõ°Ô∏è
        let isSafeToCut = true;

        if (cutIndex > -1) {
            // Wir pr√ºfen: Kommt NACH dem Cut-Index noch ein "Protected Word" (Appendix)?
            const textAfterCut = content.substring(cutIndex);

            for (const safeWord of protectedWords) {
                const safeRegex = new RegExp(`\\n\\s*${safeWord}`, 'i');
                if (textAfterCut.match(safeRegex)) {
                    // ALARM! Ein Appendix kommt nach den References.
                    isSafeToCut = false;
                    break;
                }
            }

            // Nur schneiden, wenn es sicher ist (kein Appendix dahinter)
            if (isSafeToCut) {
                content = content.substring(0, cutIndex);
            }
        }

        // Status f√ºr Debugging setzen
        item.json.cleaning_status = cutIndex > -1 
            ? (isSafeToCut ? `Cut at ${foundWord}` : `Kept References because Appendix was found`) 
            : 'Nothing to cut found';

    } else {
        item.json.cleaning_status = 'No text content available';
    }

    // Das Ergebnis IMMER speichern (auch wenn es leer ist)
    item.json.text_cleaned = content;
}

return items;
