const phase1 = $('On form submission').item.json;
const phase2 = $('Form2').item.json;
const phase3 = $('Form3').item.json;
const phase4 = $('Form4').item.json;

const allData = { ...phase1, ...phase2, ...phase3, ...phase4 };

const formatted = [
  { phase: "Grasping the Core (Most Important)", data: Object.entries(phase1) },
  { phase: "Customer Touchpoints (External Environment)", data: Object.entries(phase2) },
  { phase: "Means of Delivery (Internal Environment)", data: Object.entries(phase3) },
  { phase: "Money Flow", data: Object.entries(phase4) },
];

return [{ json: { allData, formatted } }];

const output = $('AI Canvas Generator').item.json.output;

if (output.includes('"status": "ERROR"') || output.includes('"status":"ERROR"')) {
  const errorMatch = output.match(/"message":\s*"([^"]+)"/);
  const errorMessage = errorMatch ? errorMatch[1] : 'There was an issue with your input. Please start again from the beginning.';

  return [{
    json: {
      isError: true,
      errorMessage
    }
  }];
}

return [{
  json: {
    isError: false,
    canvas: output
  }
}];
