parameters.jsCode:
const configArray = $('GetConfig').all();
const config = {};

configArray.forEach(item => {
  const name = item.json.Name;  
  const type = item.json.Type;
  let value;

  // Determine the value based on the Type field, with fallback to String Value
  if (type === 'Integer') {
    value = item.json['Integer Value'];
  } else if (type === 'String') {
    // If Type is String or not specified, use String Value
    value = item.json['String Value'];
  } else {
    // Default fallback
    value = item.json['String Value'];
  }

  config[name] = value;
});

return config;
// Result will be:
// {
//   "profile_url": "https://www.linkedin.com/in/elodetasia",
//   "posted_limit": "week",
//   "max_posts": 5
// }

const nbDaysAfter = $('FormatConfig').first().json.schedule_tweets_days_after_lk || 0;
const lkDate = DateTime.fromISO($('MergePost').first().json.Date || $('MergePost').first().json.fields.Date);

return {
  "tweet_post_date": lkDate.plus({ days: nbDaysAfter }).toISO()
};

let text = $input.first().json?.output[0]?.content[0]?.text ?? "";
let parsedJson = {};

if (text) {
  // Extract JSON from markdown code block
  // Handles ```json ... ``` or just ``` ... ```
  const jsonMatch = text.match(/```(?:json)?\s*([\s\S]*?)```/);
  if (jsonMatch && jsonMatch[1]) {
    const jsonString = jsonMatch[1].trim();
    parsedJson = JSON.parse(jsonString);
  } else {
    // If no code block, try parsing the text directly
    parsedJson = JSON.parse(text.trim());
  }
}


return { slides: parsedJson };
