const sales = {};

for (const order of items) {
  for (const item of order.json.line_items || []) {
    if (!item.sku) continue;
    sales[item.sku] = (sales[item.sku] || 0) + item.quantity;
  }
}

return Object.entries(sales).map(([sku, soldQty]) => ({
  json: { sku, soldQty }
}));

const leadDays = 7;
const safetyStock = 5;

const dailyAvg = Math.ceil( $('SKU Sales Volume').first().json.soldQty/ 7);
const reorderQty = (dailyAvg * leadDays) + safetyStock;

return [{
  json: {
    sku: $('SKU Sales Volume').first().json.sku,
    productName:$('Fetch Products').first().json.name,
    reorderQty,
    currentStock: $('Fetch Products').first().json.stock_quantity
  }
}];
