// Apply platform-specific pricing rules
for (const item of $input.all()) {
  const basePrice = item.json.newPrice;
  const sku = item.json.sku;
  const productId = item.json.productId;

  // WooCommerce: Round to 2 decimals, may need tax-inclusive pricing
  // Apply rounding to nearest .99 for psychological pricing
  let wooPrice = Math.floor(basePrice) + 0.99;

  // Shopify: Standard 2 decimal rounding
  let shopifyPrice = Math.round(basePrice * 100) / 100;

  // Ensure minimum price thresholds
  if (wooPrice < 0.99) wooPrice = 0.99;
  if (shopifyPrice < 1.00) shopifyPrice = 1.00;

  item.json.wooPrice = wooPrice;
  item.json.shopifyPrice = shopifyPrice;
  item.json.sku = sku;
  item.json.productId = productId;
}

return $input.all();

