parameters.jsCode:
/**
 * ============================================================
 * Node: Normalize Workflow JSON
 * Goal:
 *  - Clean and normalize an n8n workflow for documentation
 *  - Remove noise and sensitive data
 *  - PRESERVE the code of Code nodes (jsCode) intact
 *  - Prepare a structure that is easy for LLMs to consume
 * ============================================================
 */

/**
 * Returns an object with only the specified keys.
 * Used to avoid carrying unnecessary fields.
 */
function pick(obj, keys) {
  const out = {};
  for (const k of keys) {
    if (obj && Object.prototype.hasOwnProperty.call(obj, k)) {
      out[k] = obj[k];
    }
  }
  return out;
}

/**
 * Trims long strings to avoid blowing up the LLM context.
 * IMPORTANT: This is NOT applied to jsCode (see below).
 */
function safeString(v, max = 5000) {
  if (v == null) return v;
  const s = typeof v === "string" ? v : JSON.stringify(v);
  return s.length > max ? s.slice(0, max) + "…" : s;
}

/**
 * Converts n8n's connections structure (very nested)
 * into a flat list of edges.
 * This makes it easier to reconstruct the real execution flow.
 */
function flattenConnections(connections) {
  const edges = [];
  if (!connections || typeof connections !== "object") return edges;

  for (const fromNodeName of Object.keys(connections)) {
    const byType = connections[fromNodeName];
    if (!byType || typeof byType !== "object") continue;

    for (const connType of Object.keys(byType)) {
      const groups = byType[connType];
      if (!Array.isArray(groups)) continue;

      for (let fromOutputIndex = 0; fromOutputIndex < groups.length; fromOutputIndex++) {
        const targets = groups[fromOutputIndex];
        if (!Array.isArray(targets)) continue;

        for (const t of targets) {
          if (!t || typeof t !== "object") continue;
          edges.push({
            from: fromNodeName,
            to: t.node,
            connectionType: connType,
            fromOutputIndex,
            toInputIndex: typeof t.index === "number" ? t.index : null,
            toType: t.type || null,
          });
        }
      }
    }
  }
  return edges;
}

/**
 * =========================
 * MAIN
 * =========================
 */

// The workflow usually comes in items[0].json
const raw = items?.[0]?.json;
if (!raw) {
  return [{ json: { error: "Workflow not found in items[0].json" } }];
}

/**
 * Some n8n nodes return the workflow wrapped in { workflow: {...} }
 * Here we normalize it so we always work with the same structure.
 */
const wf =
  raw.workflow && typeof raw.workflow === "object" && raw.workflow.nodes
    ? raw.workflow
    : raw;

// Workflow name (fallbacks for safety)
const workflowName = wf.name || raw.name || "Unnamed workflow";

/**
 * Trimming configuration:
 * - DEFAULT_MAX_STRING: normal strings (URLs, text, etc.)
 * - MAX_JS_CODE: JavaScript code
 *
 * MAX_JS_CODE = null  -> jsCode is never trimmed
 * This is KEY for the two-call LLM strategy.
 */
const DEFAULT_MAX_STRING = 8000;
const MAX_JS_CODE = null;

/**
 * Node normalization:
 * - Keep only useful fields for documentation
 * - Clean long parameters
 * - Keep jsCode intact
 */
const nodes = Array.isArray(wf.nodes) ? wf.nodes : [];
const cleanedNodes = nodes.map((n) => {
  // Basic node metadata
  const base = pick(n, [
    "id",
    "name",
    "type",
    "typeVersion",
    "position",
    "disabled",
    "notes",
    "notesInFlow",
    "retryOnFail",
    "continueOnFail",
    "alwaysOutputData",
    "executeOnce",
  ]);

  // Node parameters (the most important part for documentation)
  const params = n?.parameters ?? {};

  /**
   * Controlled parameter trimming:
   * - normal strings -> standard trim
   * - parameters.jsCode -> DO NOT trim
   */
  const paramsTrimmed = JSON.parse(
    JSON.stringify(params, (key, value) => {
      if (typeof value !== "string") return value;

      if (key === "jsCode") {
        if (MAX_JS_CODE == null) return value; // keep code intact
        return safeString(value, MAX_JS_CODE);
      }

      return safeString(value, DEFAULT_MAX_STRING);
    })
  );

  base.parameters = paramsTrimmed;

  /**
   * Quick marker for triggers.
   * Useful so the LLM can identify flow entry points.
   */
  const isTrigger =
    typeof n?.type === "string" && n.type.toLowerCase().includes("trigger");
  base.isTrigger = isTrigger;

  return base;
});

/**
 * Explicit extraction of Code nodes.
 * This allows the "Code-only" LLM to work with an input
 * that is small and very focused.
 */
const codeNodes = cleanedNodes
  .filter((n) => n.type === "n8n-nodes-base.code")
  .map((n) => {
    const jsCode = n?.parameters?.jsCode ?? null;

    return {
      id: n.id,
      name: n.name,
      type: n.type,
      jsCode, // keep code intact
      lineCount: typeof jsCode === "string" ? jsCode.split("\n").length : 0,
      charCount: typeof jsCode === "string" ? jsCode.length : 0,
    };
  });

/**
 * Connection processing:
 * - edges: flat and easy-to-explain version
 * - connections: original structure (in case the LLM needs detail)
 */
const connections =
  wf.connections && typeof wf.connections === "object"
    ? wf.connections
    : {};
const edges = flattenConnections(connections);

/**
 * Minimal workflow settings useful for documentation
 */
const settings =
  wf.settings && typeof wf.settings === "object"
    ? pick(wf.settings, [
        "timezone",
        "saveDataErrorExecution",
        "saveDataSuccessExecution",
        "saveManualExecutions",
        "executionTimeout",
      ])
    : {};

/**
 * Final output:
 * - nodes: normalized nodes
 * - codeNodes: direct input for the Code-only LLM
 * - edges: flow reconstruction
 */
return [
  {
    json: {
      workflowName,
      workflowId: wf.id ?? raw.id ?? null,
      active:
        typeof wf.active === "boolean"
          ? wf.active
          : typeof raw.active === "boolean"
          ? raw.active
          : null,
      nodeCount: cleanedNodes.length,
      edgeCount: edges.length,
      nodes: cleanedNodes,
      codeNodes,
      edges,
      connections,
      settings,
    },
  },
];

/**
 * Extract Code nodes (n8n Code node type) from a previously normalized workflow payload.
 * This produces a compact output focused only on JavaScript code blocks (jsCode),
 * useful for documentation, auditing, or sending to a "code-only" LLM step.
 */

// Read the normalized nodes list from the "Normalize Workflow JSON" node output.
// Fallback to an empty array if it's missing.
const nodes = $('Normalize Workflow JSON').first().json.nodes ?? [];

/**
 * Build a minimal list of Code nodes:
 * - Keep only nodes with type "n8n-nodes-base.code"
 * - Extract basic identifiers plus the embedded JavaScript code (jsCode)
 */
const codeNodes = nodes
  .filter(n => n.type === 'n8n-nodes-base.code')
  .map(n => ({
    id: n.id,
    name: n.name,
    jsCode: n.parameters?.jsCode ?? null
  }));

/**
 * Return a summary plus the extracted code nodes:
 * - codeNodeCount: number of Code nodes found
 * - codeNodes: array with { id, name, jsCode }
 */
return [
  {
    json: {
      codeNodeCount: codeNodes.length,
      codeNodes
    }
  }
];
