// Get all items from input
const items = $input.all();
const config = $('‚öôÔ∏è Config').first().json;
const dailyLimit = config.DAILY_CONNECTION_LIMIT;

// Get today's date (start of day)
const today = new Date();
today.setHours(0, 0, 0, 0);

// Count how many connection requests were sent today
// by checking leads with Status = PENDING_CONNECTION and Last action date = today
let sentToday = 0;
for (const item of items) {
  const lastActionDateStr = item.json['Last action date'];
  const status = item.json.Status;

  if (lastActionDateStr && status === 'PENDING_CONNECTION') {
    // Parse date in format M/d/yyyy H:mm:ss
    const lastActionDate = new Date(lastActionDateStr);
    lastActionDate.setHours(0, 0, 0, 0);

    if (lastActionDate.getTime() === today.getTime()) {
      sentToday++;
    }
  }
}

// Calculate remaining quota
const remainingQuota = Math.max(0, dailyLimit - sentToday);

console.log(`Daily limit: ${dailyLimit}, Sent today: ${sentToday}, Remaining quota: ${remainingQuota}`);

// If no quota left, filter out all NEW leads
if (remainingQuota === 0) {
  return items.filter(item => item.json.Status !== 'NEW');
}

// Separate NEW leads from others
const newLeads = [];
const otherLeads = [];

for (const item of items) {
  if (item.json.Status === 'NEW') {
    newLeads.push(item);
  } else {
    otherLeads.push(item);
  }
}

// Limit NEW leads to remaining quota
const limitedNewLeads = newLeads.slice(0, remainingQuota);

console.log(`NEW leads: ${newLeads.length}, Limited to: ${limitedNewLeads.length}, Other leads: ${otherLeads.length}`);

// Return limited NEW leads + all other leads
return [...limitedNewLeads, ...otherLeads];
