={{
  (() => {
    const d = String($json.next_activity?.due_date ?? '');
    const tRaw = String($json.next_activity?.due_time ?? '');

    // If it's already a full ISO datetime
    if (tRaw.includes('T')) {
      return DateTime.fromISO(tRaw).setZone('Asia/Dubai').toISO();
    }

    // If it's only HH:mm or HH:mm:ss
    const t = tRaw.length === 5 ? `${tRaw}:00` : tRaw;
    return DateTime.fromISO(`${d}T${t}`).setZone('Asia/Dubai').toISO();
  })()
}}

={{
  (() => {
    const d = String($json.next_activity?.due_date ?? '');
    const tRaw = String($json.next_activity?.due_time ?? '');

    const start = tRaw.includes('T')
      ? DateTime.fromISO(tRaw).setZone('Asia/Dubai')
      : DateTime.fromISO(`${d}T${tRaw.length === 5 ? `${tRaw}:00` : tRaw}`).setZone('Asia/Dubai');

    const parts = String($json.next_activity?.duration ?? '00:00').split(':').map(Number);
    const hours = parts[0] || 0;
    const minutes = parts[1] || 0;

    return start.plus({ hours, minutes }).toISO();
  })()
}}
