// Calculate overall risk score based on aggregated compliance results
// Weighing factors: number of non-compliant items, severity of issues, and confidence scores

const items = $input.all();

let totalRiskScore = 0;
let nonCompliantCount = 0;
let totalIssues = 0;
let weightedConfidence = 0;

for (const item of items) {
  const data = item.json;

  // Count non-compliant items
  if (data.compliance_status === 'Non-Compliant') {
    nonCompliantCount++;
  }

  // Count total issues (severity factor)
  if (data.observed_issues && Array.isArray(data.observed_issues)) {
    totalIssues += data.observed_issues.length;
  }

  // Accumulate confidence scores
  if (data.confidence_score) {
    weightedConfidence += data.confidence_score;
  }
}

// Calculate risk score components
const itemCount = items.length || 1;
const nonComplianceRate = (nonCompliantCount / itemCount) * 100;
const averageIssuesPerItem = totalIssues / itemCount;
const averageConfidence = weightedConfidence / itemCount;

// Risk score formula (0-100 scale)
// Higher non-compliance rate and more issues = higher risk
// Higher confidence = more reliable assessment
const riskScore = Math.min(100, Math.round(
  (nonComplianceRate * 0.5) + 
  (averageIssuesPerItem * 10) + 
  ((100 - averageConfidence) * 0.3)
));

// Determine risk level
let riskLevel = 'Low';
if (riskScore >= 70) {
  riskLevel = 'Critical';
} else if (riskScore >= 50) {
  riskLevel = 'High';
} else if (riskScore >= 30) {
  riskLevel = 'Medium';
}

// Return aggregated risk assessment
return [
  {
    json: {
      overall_risk_score: riskScore,
      risk_level: riskLevel,
      total_items_evaluated: itemCount,
      non_compliant_count: nonCompliantCount,
      total_issues_identified: totalIssues,
      average_confidence: Math.round(averageConfidence),
      compliance_rate: Math.round((itemCount - nonCompliantCount) / itemCount * 100),
      evaluation_items: items.map(item => item.json)
    }
  }
];

// Performance Simulation Tool
// Simulates automotive component performance under various conditions

const componentData = typeof query === 'string' ? JSON.parse(query) : query;

// Define simulation parameters
const stressLevels = ['low', 'medium', 'high', 'extreme'];
const temperatureRanges = [
  { range: '-40°C to 0°C', condition: 'cold' },
  { range: '0°C to 40°C', condition: 'normal' },
  { range: '40°C to 80°C', condition: 'hot' },
  { range: '80°C to 120°C', condition: 'extreme_heat' }
];

// Simulate performance metrics
const performanceMetrics = {
  component: componentData.component_id || componentData.name || 'Unknown Component',
  stress_test_results: {},
  temperature_performance: {},
  load_scenarios: {},
  potential_failure_points: [],
  overall_performance_score: 0
};

// Stress test simulation
stressLevels.forEach(level => {
  const baseScore = Math.random() * 100;
  const degradation = stressLevels.indexOf(level) * 15;
  performanceMetrics.stress_test_results[level] = {
    score: Math.max(0, baseScore - degradation).toFixed(2),
    status: baseScore - degradation > 70 ? 'Pass' : 'Fail'
  };
});

// Temperature variation simulation
temperatureRanges.forEach(temp => {
  const efficiency = (Math.random() * 30 + 70).toFixed(2);
  performanceMetrics.temperature_performance[temp.condition] = {
    range: temp.range,
    efficiency_percentage: efficiency,
    status: efficiency > 75 ? 'Optimal' : efficiency > 60 ? 'Acceptable' : 'Poor'
  };
});

// Load scenario simulation
performanceMetrics.load_scenarios = {
  normal_load: { capacity: '100%', status: 'Pass' },
  peak_load: { capacity: (Math.random() * 30 + 70).toFixed(2) + '%', status: 'Pass' },
  overload: { capacity: (Math.random() * 40 + 40).toFixed(2) + '%', status: Math.random() > 0.5 ? 'Pass' : 'Fail' }
};

// Identify potential failure points
if (performanceMetrics.stress_test_results.extreme.status === 'Fail') {
  performanceMetrics.potential_failure_points.push('Component may fail under extreme stress conditions');
}
if (parseFloat(performanceMetrics.temperature_performance.extreme_heat.efficiency_percentage) < 60) {
  performanceMetrics.potential_failure_points.push('Reduced efficiency in extreme heat conditions');
}
if (performanceMetrics.load_scenarios.overload.status === 'Fail') {
  performanceMetrics.potential_failure_points.push('Component not rated for sustained overload conditions');
}

// Calculate overall performance score
const stressScores = Object.values(performanceMetrics.stress_test_results).map(r => parseFloat(r.score));
const avgStressScore = stressScores.reduce((a, b) => a + b, 0) / stressScores.length;
const tempScores = Object.values(performanceMetrics.temperature_performance).map(t => parseFloat(t.efficiency_percentage));
const avgTempScore = tempScores.reduce((a, b) => a + b, 0) / tempScores.length;
performanceMetrics.overall_performance_score = ((avgStressScore + avgTempScore) / 2).toFixed(2);

return JSON.stringify(performanceMetrics, null, 2);
