const input = $input.first().json;

// Data is inside body for webhook
const body = input.body || input;
const data = body.data || {};
const response = data.response || {};

const name = response.Name || 'Unknown';
const nameParts = name.split(' ');

return [{
  json: {
    feedType: body.type || '',
    email: body.reviewer_email || response.Email || '',
    name: name,
    firstName: nameParts[0] || 'Unknown',
    lastName: nameParts.slice(1).join(' ') || '',
    phone: response.contact_number || '',
    position: response.position || '',
    organization: response.organization_name || '',
    photo: response.photo || '',
    rating: response.value || '',
    ratingType: response.type || '',
    comment: response.comment || response.other_text || '',
    videoUrl: data.video_url || '',
    audioUrl: data.audio_url || '',
    thumbnailUrl: data.thumbnail_url || '',
    feedId: data.feed_id || '',
    feedUrl: data.feed_url || '',
    formName: data.feed_form?.name || '',
    formUrl: data.feed_form?.url || '',
    projectName: data.project?.name || '',
    projectLogo: data.project?.logo || '',
    createdAt: data.created_at || '',
    consent: response.consent || false,
    liveMode: data.live_mode || false
  }
}];

