const now = new Date();

return items.map((item) => {
  const r = item.json || {};

  const recordId = r.id;
  const firstName = String(r["First Name"] ?? r["First  Name"] ?? "").trim();
  const email = String(r["Email"] ?? "").trim();

  const phoneRaw = String(r["Phone Number"] ?? "").trim();
  const phoneE164 = phoneRaw ? (phoneRaw.startsWith("+") ? phoneRaw : "+" + phoneRaw) : "";

  const outreachSentAtRaw = r["Outreach Sent At"];
  const sentAt = outreachSentAtRaw ? new Date(outreachSentAtRaw) : null;

  const rem1Raw = r["Reminder 1 Sent"];
  const rem2Raw = r["Reminder 2 Sent"];

  const rem1Sent = rem1Raw === true || rem1Raw === "checked";
  const rem2Sent = rem2Raw === true || rem2Raw === "checked";

  let hoursSinceOutreach = null;

  // 0 = none, 1 = reminder1, 2 = reminder2
  let routeCode = 0;

  if (sentAt && !isNaN(sentAt.getTime())) {
    hoursSinceOutreach = Math.floor((now.getTime() - sentAt.getTime()) / (1000 * 60 * 60));

    // Priority: reminder2 wins
    if (hoursSinceOutreach >= 96 && !rem2Sent) routeCode = 2;
    else if (hoursSinceOutreach >= 48 && !rem1Sent) routeCode = 1;
  }

  return {
    json: {
      recordId,
      email,
      firstName,
      phoneE164,
      hasEmail: !!email,
      hasPhone: !!phoneE164,
      hoursSinceOutreach,
      routeCode,
      applyLink: "https://www.morestarts.co.uk/welding-course/"
    }
  };
});
