parameters.jsCode:
function safeStr(v) {
  return (v ?? '').toString().trim();
}

function normalizeBool(v) {
  return v === true || v === 'checked' || v === 'true' || v === 1;
}

function toE164(phoneRaw) {
  const p = safeStr(phoneRaw);
  if (!p) return '';
  return p.startsWith('+') ? p : `+${p}`;
}

return items.map((item) => {
  const r = item.json || {};
  const f = r.fields || {};

  const eventType = safeStr(r.eventType); // 'info' | 'course'
  const recordId = r.id;

  const firstName = safeStr(f['First  Name'] ?? f['First Name'] ?? f['First Name ']);
  const email = safeStr(f['Email']);
  const phoneE164 = toE164(f['Phone Number']);

  const referrerEmail = safeStr(f['Referrer Email'] ?? f['Referring Person Email']);

  const infoOutcome = safeStr(f['Info Event Outcome']);   // Attended / Did Not Attend / Rescheduled
  const courseOutcome = safeStr(f['Course Outcome']);     // Started / Did Not Start/Withdrawn / Completed

  // Sent flags (candidate)
  const postInfoAttendedSent = normalizeBool(f['Post-Info Attended Sent']);
  const postInfoNoShowSent = normalizeBool(f['Post-Info NoShow Sent']);
  const courseOutcomeMsgSent = normalizeBool(f['Course Outcome Message Sent']);

  // Sent flags (referrer)
  const referrerInfoNotified = normalizeBool(f['Referrer Notified – Info Outcome']);
  const referrerCourseStartNotified = normalizeBool(f['Referrer Notified – Course Start']); // NEW FIELD
  const referrerCourseEndNotified = normalizeBool(f['Referrer Notified – Course Outcome']); // reuse existing for end notification

  // Normalize course outcome to a key for templates
  let courseOutcomeKey = '';
  if (courseOutcome === 'Started') courseOutcomeKey = 'started';
  else if (courseOutcome === 'Did Not Start/Withdrawn') courseOutcomeKey = 'not_started_withdrawn';
  else if (courseOutcome === 'Completed') courseOutcomeKey = 'completed';
  else courseOutcomeKey = courseOutcome ? 'other' : '';

  // Decide ONE action route (candidate messaging)
  let action = 'none';

  if (eventType === 'info') {
    if (infoOutcome === 'Attended' && !postInfoAttendedSent) action = 'info_attended';
    else if (infoOutcome === 'Did Not Attend' && !postInfoNoShowSent) action = 'info_noshow';
    // Rescheduled => none
  }

  if (eventType === 'course') {
    if (courseOutcomeKey && !courseOutcomeMsgSent) action = 'course';
  }

  // Referrer notifications (separate from candidate messaging)
  const notifyReferrerInfo =
    eventType === 'info' &&
    !!referrerEmail &&
    !referrerInfoNotified &&
    (infoOutcome === 'Attended' || infoOutcome === 'Did Not Attend');

  // NEW: referrer course start notification (only when Started)
  const notifyReferrerCourseStart =
    eventType === 'course' &&
    !!referrerEmail &&
    !referrerCourseStartNotified &&
    courseOutcomeKey === 'started';

  // NEW: referrer course end notification (only when Completed OR Withdrawn/Did Not Start)
  const notifyReferrerCourseEnd =
    eventType === 'course' &&
    !!referrerEmail &&
    !referrerCourseEndNotified &&
    (courseOutcomeKey === 'completed' || courseOutcomeKey === 'not_started_withdrawn');

  return {
    json: {
      recordId,
      eventType,
      action,

      firstName,
      email,
      phoneE164,
      referrerEmail,

      infoOutcome,
      courseOutcome,
      courseOutcomeKey,

      hasEmail: !!email,
      hasPhone: !!phoneE164,

      notifyReferrerInfo,
      notifyReferrerCourseStart,
      notifyReferrerCourseEnd,

      applyLink: 'https://www.morestarts.co.uk/welding-course/',
      senderName: 'Morestarts',
      senderEmail: 'user@example.com'
    }
  };
});

