const normalized = [];

for (const item of items) {
  const source =
    item.json.source ||
    item.json.provider ||
    item.json.news ||
    'Unknown';

  const articles = item.json.articles || [];

  for (const article of articles) {
    normalized.push({
      json: {
        source,
        title: article.title || '',
        description: article.description || '',
        content: article.content || '',
        url: article.url || '',
        image: article.image || null
      }
    });
  }
}

return normalized;

const articles = [];
const errors = [];

for (const item of items) {
  if (item.json.title) {
    articles.push(item.json);
  } else if (item.json.error) {
    errors.push(item.json);
  }
}

return [{
  json: {
    articles,
    errors,
    generatedAt: new Date().toISOString()
  }
}];

