// Normalize AI response before sending to Messenger
const input_data = $input.first().json;
const ai_text = input_data.output || input_data.text || '';
let clean_text = ai_text.trim();

// Limit message length (Messenger: 2000 characters)
if (clean_text.length > 1900) {
  clean_text = clean_text.slice(0, 1897) + '...';
}

// Remove unsupported markdown formatting
clean_text = clean_text
  .replace(/\*\*(.+?)\*\*/g, '$1')   // remove bold
  .replace(/\*(.+?)\*/g, '$1')       // remove italic
  .replace(/```[\s\S]*?```/g, block => block.replace(/```\w*\n?/g, '')) // remove code blocks
  .replace(/`(.+?)`/g, '$1');        // remove inline code

// Return final result
return {
  json: {
    page_message: clean_text
  }
};
