// Convert single output to 3 separate items for Thumbnail, Image 1, and Image 2

const inputData = $input.all()[0].json.output;

// Create array of 3 items
const outputItems = [
  {
    json: {
      imageType: "thumbnail",
      imagePrompt: inputData["Thumbnail Prompt"],
      altText: inputData["Alt Text Thumbnail Image"]
    }
  },
  {
    json: {
      imageType: "image1", 
      imagePrompt: inputData["Image Prompt 1"],
      altText: inputData["Alt Text Image 1"]
    }
  },
  {
    json: {
      imageType: "image2",
      imagePrompt: inputData["Image Prompt 2"], 
      altText: inputData["Alt Text Image 2"]
    }
  }
];

return outputItems;

